static int metal_init_page_sizes(void)
{
    FILE *log = fopen("/tmp/logs/655.txt", "a");
    if (log) fprintf(log, "ENTRY metal_init_page_sizes\n");

    const int max_sizes = MAX_PAGE_SIZES - 1;
    long sizes[max_sizes];

    /* Determine system page size. */
    sizes[0] = getpagesize();
    if (log) fprintf(log, "getpagesize() -> %ld\n", sizes[0]);
    if (sizes[0] <= 0) {
        if (log) fprintf(log, "failed to get page size (<=0): %ld\n", sizes[0]);
        if (log) fclose(log);
        metal_log(METAL_LOG_ERROR, "failed to get page size\n");
        return -EINVAL;
    }

    _metal.page_size  = sizes[0];
    _metal.page_shift = metal_log2(sizes[0]);
    if (log) {
        fprintf(log, "set _metal.page_size=%ld _metal.page_shift=%d\n",
                _metal.page_size, _metal.page_shift);
        fprintf(log, "_metal.tmp_path pointer=%p ", (void *)_metal.tmp_path);
        if (_metal.tmp_path) fprintf(log, "_metal.tmp_path content=\"%.200s\"\n", _metal.tmp_path);
        else fprintf(log, "_metal.tmp_path content=NULL\n");
    }

    /* Call metal_add_page_size for the base page size. */
    {
        char *path0 = _metal.tmp_path;
        if (log) fprintf(log, "Calling metal_add_page_size(path=%p, shift=%d, flags=0)\n",
                         (void *)path0, _metal.page_shift);
        metal_add_page_size(path0, _metal.page_shift, 0);
    }

#ifdef HAVE_HUGETLBFS_H
#ifndef MAP_HUGE_SHIFT
    /* System does not support multiple huge page sizes. */
    sizes[0] = gethugepagesize();
    if (log) fprintf(log, "gethugepagesize() -> %ld\n", sizes[0]);
    if (sizes[0] > 0) {
        char *hp = hugetlbfs_find_path();
        if (log) {
            fprintf(log, "hugetlbfs_find_path() -> %p ", (void *)hp);
            if (hp) fprintf(log, "\"%.200s\"\n", hp);
            else fprintf(log, "NULL\n");
            fprintf(log, "Calling metal_add_page_size(path=%p, shift=%d, flags=MAP_HUGETLB)\n",
                    (void *)hp, metal_log2(sizes[0]));
        }
        metal_add_page_size(hp, metal_log2(sizes[0]), MAP_HUGETLB);
    }
#else
    if (gethugepagesize() >= 0) {
        int i, count;

        /* System supports multiple huge page sizes. */
        count = gethugepagesizes(sizes, max_sizes);
        if (log) fprintf(log, "gethugepagesizes() -> count=%d\n", count);
        for (i = 0; i < count; i++) {
            int shift = metal_log2(sizes[i]);

            if (log) fprintf(log, "sizes[%d]=%ld shift=%d\n", i, sizes[i], shift);
            if ((shift & MAP_HUGE_MASK) != shift) {
                if (log) fprintf(log, "skip sizes[%d] due to mask mismatch\n", i);
                continue;
            }

            char *hp = hugetlbfs_find_path_for_size(sizes[i]);
            if (log) {
                fprintf(log, "hugetlbfs_find_path_for_size(%ld) -> %p ", sizes[i], (void *)hp);
                if (hp) fprintf(log, "\"%.200s\"\n", hp);
                else fprintf(log, "NULL\n");
                fprintf(log, "Calling metal_add_page_size(path=%p, shift=%d, flags=MAP_HUGETLB | (shift<<MAP_HUGE_SHIFT))\n",
                        (void *)hp, shift);
            }
            metal_add_page_size(hp, shift, (MAP_HUGETLB | (shift << MAP_HUGE_SHIFT)));
        }
    }
#endif
#endif

    /* Finally sort the resulting array by size. */
    if (log) fprintf(log, "Before qsort: _metal.num_page_sizes=%d sizeof(struct metal_page_size)=%zu\n",
                     _metal.num_page_sizes, sizeof(struct metal_page_size));
    qsort(_metal.page_sizes, _metal.num_page_sizes,
          sizeof(struct metal_page_size), metal_pagesize_compare);

    if (log) {
        fprintf(log, "After qsort: _metal.num_page_sizes=%d\n", _metal.num_page_sizes);
        fprintf(log, "EXIT metal_init_page_sizes returning 0\n");
        fclose(log);
    }

    return 0;
}
