static int metal_init_page_sizes(void)
{
    const int max_sizes = MAX_PAGE_SIZES - 1;
    long sizes[max_sizes];
    FILE *log = fopen("/tmp/logs/655.txt", "a");

    if (log) {
        fprintf(log, "ENTER: metal_init_page_sizes()\n");
    }

    /* Determine system page size. */
    sizes[0] = getpagesize();
    if (log) {
        fprintf(log, "getpagesize() -> %ld\n", sizes[0]);
    }
    if (sizes[0] <= 0) {
        if (log) {
            fprintf(log, "ERROR: failed to get page size (<=0). Returning -EINVAL\n");
            fflush(log);
            fclose(log);
        }
        metal_log(METAL_LOG_ERROR, "failed to get page size\n");
        return -EINVAL;
    }

    _metal.page_size  = sizes[0];
    _metal.page_shift = metal_log2(sizes[0]);
    if (log) {
        fprintf(log, "base page: size=%ld, shift=%d, tmp_path=%p\n",
                sizes[0], _metal.page_shift, (void *)_metal.tmp_path);
        if (_metal.tmp_path)
            fprintf(log, "tmp_path str: %s\n", _metal.tmp_path);
    }

    /* Log before calling metal_add_page_size for base page */
    if (log) {
        fprintf(log, "CALL: metal_add_page_size(path=_metal.tmp_path, shift=%d, flags=0)\n",
                _metal.page_shift);
        fflush(log);
    }
    metal_add_page_size(_metal.tmp_path, _metal.page_shift, 0);

#ifdef HAVE_HUGETLBFS_H
#ifndef MAP_HUGE_SHIFT
    /* System does not support multiple huge page sizes. */
    sizes[0] = gethugepagesize();
    if (log) {
        fprintf(log, "gethugepagesize() -> %ld\n", sizes[0]);
        fflush(log);
    }
    if (sizes[0] > 0) {
        char *hpath = hugetlbfs_find_path();
        if (log) {
            fprintf(log, "hugetlbfs_find_path() -> %p\n", (void *)hpath);
            if (hpath)
                fprintf(log, "hugetlbfs path str: %s\n", hpath);
            fprintf(log, "CALL: metal_add_page_size(path=hugetlbfs_find_path(), shift=%d, flags=MAP_HUGETLB)\n",
                    metal_log2(sizes[0]));
            fflush(log);
        }
        metal_add_page_size(hpath,
                            metal_log2(sizes[0]),
                            MAP_HUGETLB);
    }
#else
    if (gethugepagesize() >= 0) {
        int i, count;

        /* System supports multiple huge page sizes. */
        count = gethugepagesizes(sizes, max_sizes);
        if (log) {
            fprintf(log, "gethugepagesizes() -> count=%d\n", count);
            for (i = 0; i < count; i++)
                fprintf(log, "  sizes[%d]=%ld\n", i, sizes[i]);
            fflush(log);
        }
        for (i = 0; i < count; i++) {
            int shift = metal_log2(sizes[i]);

            if (log) {
                fprintf(log, "loop i=%d: sizes[i]=%ld, shift=%d\n", i, sizes[i], shift);
                fflush(log);
            }

            if ((shift & MAP_HUGE_MASK) != shift) {
                if (log) {
                    fprintf(log, "  skip: (shift & MAP_HUGE_MASK) != shift -> %d\n", shift);
                    fflush(log);
                }
                continue;
            }

            char *hpath = hugetlbfs_find_path_for_size(sizes[i]);
            if (log) {
                fprintf(log, "  hugetlbfs_find_path_for_size(%ld) -> %p\n", sizes[i], (void *)hpath);
                if (hpath)
                    fprintf(log, "  hugetlbfs path str: %s\n", hpath);
                fprintf(log, "  CALL: metal_add_page_size(path=hugetlbfs_find_path_for_size(...), shift=%d, flags=MAP_HUGETLB|(...))\n",
                        shift);
                fflush(log);
            }

            metal_add_page_size(
                hpath,
                shift, (MAP_HUGETLB |
                (shift << MAP_HUGE_SHIFT)));
        }
    }
#endif
#endif

    /* Finally sort the resulting array by size. */
    if (log) {
        fprintf(log, "Before qsort: num_page_sizes=%d\n", _metal.num_page_sizes);
        fflush(log);
    }
    qsort(_metal.page_sizes, _metal.num_page_sizes,
          sizeof(struct metal_page_size), metal_pagesize_compare);

    if (log) {
        fprintf(log, "EXIT: metal_init_page_sizes() -> returning 0, num_page_sizes=%d\n",
                _metal.num_page_sizes);
        fflush(log);
        fclose(log);
    }

    return 0;
}
