void metal_io_init(struct metal_io_region *io, void *virt,
          const metal_phys_addr_t *physmap, size_t size,
          unsigned int page_shift, unsigned int mem_flags,
          const struct metal_io_ops *ops)
{
#include <stdio.h>
#include <limits.h>

    /* Minimal probe logging to capture control flow and state. */
    FILE *log = fopen("/tmp/logs/585.txt", "a");
    if (log) {
        fprintf(log, "metal_io_init ENTER\n");
        fprintf(log, "  io=%p\n", (void *)io);
        fprintf(log, "  virt=%p\n", virt);
        fprintf(log, "  physmap=%p\n", (const void *)physmap);
        fprintf(log, "  size=%zu\n", size);
        fprintf(log, "  page_shift=%u\n", page_shift);
        fprintf(log, "  mem_flags=0x%x\n", mem_flags);
        fprintf(log, "  ops=%p\n", (const void *)ops);
        if (ops) {
            /* We cannot print fields portably here (unknown struct layout),
             * but recording that ops was non-NULL is useful evidence. */
            fprintf(log, "  ops: provided (non-NULL)\n");
        } else {
            fprintf(log, "  ops: NULL (will use nops)\n");
        }
        fflush(log);
    }

    const struct metal_io_ops nops = {
        NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL
    };

    io->virt = virt;
    if (log) {
        fprintf(log, "  assigned io->virt=%p\n", io->virt);
        fflush(log);
    }

    io->physmap = physmap;
    if (log) {
        fprintf(log, "  assigned io->physmap=%p\n", (const void *)io->physmap);
        fflush(log);
    }

    io->size = size;
    if (log) {
        fprintf(log, "  assigned io->size=%zu\n", io->size);
        fflush(log);
    }

    io->page_shift = page_shift;
    if (log) {
        fprintf(log, "  assigned io->page_shift=%u\n", io->page_shift);
        fflush(log);
    }

    if (page_shift >= sizeof(io->page_mask) * CHAR_BIT) {
        /* avoid overflow */
        io->page_mask = -1UL;
        if (log) {
            fprintf(log, "  page_shift >= %zu -> set io->page_mask = -1UL (0x%lx)\n",
                    sizeof(io->page_mask) * (size_t)CHAR_BIT, io->page_mask);
            fflush(log);
        }
    } else {
        io->page_mask = (1UL << page_shift) - 1UL;
        if (log) {
            fprintf(log, "  page_shift < %zu -> set io->page_mask = (1UL << %u) - 1 = 0x%lx\n",
                    sizeof(io->page_mask) * (size_t)CHAR_BIT, page_shift, io->page_mask);
            fflush(log);
        }
    }

    io->mem_flags = mem_flags;
    if (log) {
        fprintf(log, "  assigned io->mem_flags=0x%x\n", io->mem_flags);
        fflush(log);
    }

    io->ops = ops ? *ops : nops;
    if (log) {
        fprintf(log, "  io->ops assigned from %s\n", ops ? "provided ops" : "nops (defaults)");
        /* Log the address of the ops structure source for traceability. */
        fprintf(log, "  source ops pointer = %p\n", (const void *)ops);
        fprintf(log, "metal_io_init EXIT (about to call metal_sys_io_mem_map)\n\n");
        fclose(log);
    }

    /* Preserve original behavior */
    metal_sys_io_mem_map(io);
}
