void metal_io_init(struct metal_io_region *io, void *virt,
              const metal_phys_addr_t *physmap, size_t size,
              unsigned int page_shift, unsigned int mem_flags,
              const struct metal_io_ops *ops)
{
    const struct metal_io_ops nops = {
        NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL
    };

    /* Local, block-scoped opaque FILE type and stdio prototypes so this
     * function can emit logs even if stdio.h is not visible at the point of
     * replacement. These are shadowing declarations and are legal in block
     * scope. */
    typedef struct __local_FILE FILE;
    FILE *fopen(const char *, const char *);
    int fprintf(FILE *, const char *, ...);
    int fclose(FILE *);
    int mkdir(const char *, unsigned int);

    /* Attempt to create the logs directory; ignore errors. */
    (void)mkdir("/tmp/logs", 0777U);

    FILE *log = fopen("/tmp/logs/585.txt", "a");
    if (log)
    {
        (void)fprintf(log, "metal_io_init: entered\n");
        (void)fprintf(log, "  params: io=%p, virt=%p, physmap=%p, size=%zu, page_shift=%u, mem_flags=%u, ops=%p\n",
                      (void *)io, virt, (const void *)physmap, size, page_shift, mem_flags, (const void *)ops);
    }

    io->virt = virt;
    io->physmap = physmap;
    io->size = size;
    io->page_shift = page_shift;

    if (log)
    {
        (void)fprintf(log, "  computing page_mask with page_shift=%u\n", page_shift);
    }

    if (page_shift >= sizeof(io->page_mask) * CHAR_BIT)
        /* avoid overflow */
        io->page_mask = -1UL;
    else
        io->page_mask = (1UL << page_shift) - 1UL;

    if (log)
    {
        (void)fprintf(log, "  page_mask result = 0x%lX\n", (unsigned long)io->page_mask);
    }

    io->mem_flags = mem_flags;
    io->ops = ops ? *ops : nops;

    if (log)
    {
        (void)fprintf(log, "  about to call metal_sys_io_mem_map(io=%p)\n", (void *)io);
        (void)fclose(log);
        log = (FILE *)0;
    }

    /* Preserve original behavior: perform system mapping. */
    metal_sys_io_mem_map(io);

    /* Reopen and log completion so that the traced run shows exit. */
    log = fopen("/tmp/logs/585.txt", "a");
    if (log)
    {
        (void)fprintf(log, "metal_io_init: completed\n");
        (void)fclose(log);
    }
}
