static int metal_irq_allocate(int irq_base, int irq_num)
{
	struct metal_list *node;
	struct metal_irq_controller *cntr;
	int irq_tocheck = irq_base, irq_end_tocheck;

	/* Logging setup: append logs so multiple calls are preserved */
	{
		/* Attempt to log control flow and state. Relying on stdio being available
		   in the translation unit where this function will be compiled. */
		FILE *logf = fopen("/tmp/logs/645.txt", "a");
		if (logf) {
			fprintf(logf, "ENTER: metal_irq_allocate(irq_base=%d, irq_num=%d)\n",
				irq_base, irq_num);
			fclose(logf);
		}
	}

	if (irq_num == 0) {
		/* Log the early return for zero-sized allocation */
		{
			FILE *logf = fopen("/tmp/logs/645.txt", "a");
			if (logf) {
				fprintf(logf, "EXIT: irq_num==0 -> return METAL_IRQ_ANY (%d)\n",
					(int)METAL_IRQ_ANY);
				fclose(logf);
			}
		}
		return METAL_IRQ_ANY;
	}
	if (irq_tocheck == METAL_IRQ_ANY) {
		irq_tocheck = 0;
		/* Log normalization of METAL_IRQ_ANY input */
		{
			FILE *logf = fopen("/tmp/logs/645.txt", "a");
			if (logf) {
				fprintf(logf, "Normalized irq_tocheck from METAL_IRQ_ANY to 0\n");
				fclose(logf);
			}
		}
	}
	irq_end_tocheck = irq_tocheck + irq_num;

	/* Log the initial search window */
	{
		FILE *logf = fopen("/tmp/logs/645.txt", "a");
		if (logf) {
			fprintf(logf, "Initial window: irq_tocheck=%d, irq_end_tocheck=%d\n",
				irq_tocheck, irq_end_tocheck);
			fclose(logf);
		}
	}

	metal_list_for_each(&irq_cntrs, node) {
		int cntr_irq_base, cntr_irq_end;

		cntr = metal_container_of(node,
				  struct metal_irq_controller, node);
		cntr_irq_base = cntr->irq_base;
		cntr_irq_end = cntr_irq_base + cntr->irq_num;

		/* Log information about the controller being inspected */
		{
			FILE *logf = fopen("/tmp/logs/645.txt", "a");
			if (logf) {
				fprintf(logf, "Inspecting controller at %p: irq_base=%d, irq_num=%d, irq_end=%d\n",
					(void *)cntr, cntr_irq_base, cntr->irq_num, cntr_irq_end);
				fclose(logf);
			}
		}

		if (irq_tocheck < cntr_irq_end &&
		    irq_end_tocheck > cntr_irq_base) {
			/* Overlap detected */
			{
				FILE *logf = fopen("/tmp/logs/645.txt", "a");
				if (logf) {
					fprintf(logf, "Overlap: search window [%d,%d) intersects controller [%d,%d)\n",
						irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
					fclose(logf);
				}
			}

			if (irq_base != METAL_IRQ_ANY) {
				/* IRQ has been allocated */
				{
					FILE *logf = fopen("/tmp/logs/645.txt", "a");
					if (logf) {
						fprintf(logf, "Conflict with requested irq_base=%d -> return METAL_IRQ_ANY (%d)\n",
							irq_base, (int)METAL_IRQ_ANY);
						fclose(logf);
					}
				}
				return METAL_IRQ_ANY;
			}
			irq_tocheck = cntr_irq_end;
			irq_end_tocheck = irq_tocheck + irq_num;

			/* Log the window adjustment after skipping a controller */
			{
				FILE *logf = fopen("/tmp/logs/645.txt", "a");
				if (logf) {
					fprintf(logf, "Adjusted window: irq_tocheck=%d, irq_end_tocheck=%d\n",
						irq_tocheck, irq_end_tocheck);
					fclose(logf);
				}
			}
		}
	}

	/* Final decision: log and return the computed irq_tocheck */
	{
		FILE *logf = fopen("/tmp/logs/645.txt", "a");
		if (logf) {
			fprintf(logf, "EXIT: returning irq_tocheck=%d\n", irq_tocheck);
			fclose(logf);
		}
	}

	return irq_tocheck;
}
