static int metal_irq_allocate(int irq_base, int irq_num)
{
	struct metal_list *node;
	struct metal_irq_controller *cntr;
	int irq_tocheck = irq_base, irq_end_tocheck;
	/* Open log file for tracing control flow and state */
	{
		FILE *log = fopen("/tmp/logs/645.txt", "a");
		if (log) {
			fprintf(log, "ENTER metal_irq_allocate: irq_base=%d, irq_num=%d\n", irq_base, irq_num);
			fprintf(log, "  initial irq_tocheck=%d\n", irq_tocheck);
			fclose(log);
		}
	}

	if (irq_num == 0) {
		FILE *log = fopen("/tmp/logs/645.txt", "a");
		if (log) {
			fprintf(log, "  early return: irq_num==0 -> METAL_IRQ_ANY\n");
			fclose(log);
		}
		return METAL_IRQ_ANY;
	}
	if (irq_tocheck == METAL_IRQ_ANY) {
		FILE *log = fopen("/tmp/logs/645.txt", "a");
		if (log) {
			fprintf(log, "  irq_tocheck==METAL_IRQ_ANY, normalizing to 0\n");
			fclose(log);
		}
		irq_tocheck = 0;
	}
	irq_end_tocheck = irq_tocheck + irq_num;
	{
		FILE *log = fopen("/tmp/logs/645.txt", "a");
		if (log) {
			fprintf(log, "  irq_end_tocheck=%d (irq_tocheck=%d + irq_num=%d)\n", irq_end_tocheck, irq_tocheck, irq_num);
			fclose(log);
		}
	}

	metal_list_for_each(&irq_cntrs, node) {
		int cntr_irq_base, cntr_irq_end;
		/* Log the node pointer to help reproduce traversal and const-pointer scenario */
		{
			FILE *log = fopen("/tmp/logs/645.txt", "a");
			if (log) {
				fprintf(log, "  visiting node=%p\n", (void *)node);
				fclose(log);
			}
		}

		cntr = metal_container_of(node,
					  struct metal_irq_controller, node);
		/* Log controller pointer and its irq range */
		{
			FILE *log = fopen("/tmp/logs/645.txt", "a");
			if (log) {
				fprintf(log, "    cntr=%p\n", (void *)cntr);
				if (cntr) {
					fprintf(log, "    cntr->irq_base=%d, cntr->irq_num=%d\n", cntr->irq_base, cntr->irq_num);
				} else {
					fprintf(log, "    cntr is NULL (unexpected)\n");
				}
				fclose(log);
			}
		}

		cntr_irq_base = cntr->irq_base;
		cntr_irq_end = cntr_irq_base + cntr->irq_num;
		{
			FILE *log = fopen("/tmp/logs/645.txt", "a");
			if (log) {
				fprintf(log, "    cntr range = [%d, %d)\n", cntr_irq_base, cntr_irq_end);
				fprintf(log, "    checking overlap: irq_tocheck=%d, irq_end_tocheck=%d\n", irq_tocheck, irq_end_tocheck);
				fclose(log);
			}
		}

		if (irq_tocheck < cntr_irq_end &&
		    irq_end_tocheck > cntr_irq_base) {
			if (irq_base != METAL_IRQ_ANY) {
				FILE *log = fopen("/tmp/logs/645.txt", "a");
				if (log) {
					fprintf(log, "    overlap detected and specific irq requested -> returning METAL_IRQ_ANY\n");
					fclose(log);
				}
				/* IRQ has been allocated */
				return METAL_IRQ_ANY;
			}
			/* move the candidate base past this controller's range */
			irq_tocheck = cntr_irq_end;
			irq_end_tocheck = irq_tocheck + irq_num;
			{
				FILE *log = fopen("/tmp/logs/645.txt", "a");
				if (log) {
					fprintf(log, "    overlap detected, advancing irq_tocheck -> %d, irq_end_tocheck -> %d\n", irq_tocheck, irq_end_tocheck);
					fclose(log);
				}
			}
		}
	}

	{
		FILE *log = fopen("/tmp/logs/645.txt", "a");
		if (log) {
			fprintf(log, "EXIT metal_irq_allocate: returning %d\n", irq_tocheck);
			fclose(log);
		}
	}
	return irq_tocheck;
}
