static int metal_irq_allocate(int irq_base, int irq_num)
{
#include <stdio.h>
#include <sys/stat.h>
#include <sys/types.h>

    struct metal_list *node;
    struct metal_irq_controller *cntr;
    int irq_tocheck = irq_base, irq_end_tocheck;
    FILE *log = NULL;

    /* Ensure log directory exists (ignore errors) */
    (void)mkdir("/tmp/logs", 0755);
    log = fopen("/tmp/logs/645.txt", "a");
    if (log) {
        fprintf(log, "ENTER metal_irq_allocate: irq_base=%d irq_num=%d\n", irq_base, irq_num);
    }

    if (irq_num == 0) {
        if (log) {
            fprintf(log, "early return: irq_num == 0 -> METAL_IRQ_ANY\n");
            fclose(log);
        }
        return METAL_IRQ_ANY;
    }
    if (irq_tocheck == METAL_IRQ_ANY) {
        if (log) {
            fprintf(log, "irq_tocheck was METAL_IRQ_ANY, setting to 0\n");
        }
        irq_tocheck = 0;
    }
    irq_end_tocheck = irq_tocheck + irq_num;
    if (log) {
        fprintf(log, "initial range: irq_tocheck=%d irq_end_tocheck=%d\n", irq_tocheck, irq_end_tocheck);
    }

    metal_list_for_each(&irq_cntrs, node) {
        int cntr_irq_base, cntr_irq_end;

        /* Capture the controller pointer and its state */
        cntr = metal_container_of(node,
                      struct metal_irq_controller, node);
        if (log) {
            fprintf(log, "node=%p cntr=%p\n", (void *)node, (void *)cntr);
        }

        /* Read-only access to controller members (this is the scenario of interest) */
        cntr_irq_base = cntr->irq_base;
        cntr_irq_end = cntr_irq_base + cntr->irq_num;
        if (log) {
            fprintf(log, "cntr->irq_base=%d cntr->irq_num=%d cntr_irq_end=%d\n",
                    cntr_irq_base, cntr->irq_num, cntr_irq_end);
            fprintf(log, "checking overlap: irq_tocheck=%d irq_end_tocheck=%d vs cntr range [%d,%d)\n",
                    irq_tocheck, irq_end_tocheck, cntr_irq_base, cntr_irq_end);
        }

        if (irq_tocheck < cntr_irq_end &&
            irq_end_tocheck > cntr_irq_base) {
            if (irq_base != METAL_IRQ_ANY) {
                /* IRQ has been allocated */
                if (log) {
                    fprintf(log, "overlap detected and specific irq requested -> return METAL_IRQ_ANY\n");
                    fclose(log);
                }
                return METAL_IRQ_ANY;
            }
            irq_tocheck = cntr_irq_end;
            irq_end_tocheck = irq_tocheck + irq_num;
            if (log) {
                fprintf(log, "overlap detected, advancing irq_tocheck -> %d, irq_end_tocheck -> %d\n",
                        irq_tocheck, irq_end_tocheck);
            }
        }
    }
    if (log) {
        fprintf(log, "EXIT metal_irq_allocate: returning irq_tocheck=%d\n", irq_tocheck);
        fclose(log);
    }
    return irq_tocheck;
}
