int metal_irq_register(int irq,
                       metal_irq_handler irq_handler,
                       void *arg)
{
    FILE *f = fopen("/tmp/logs/265.txt", "a");
    if (f == NULL) {
        /* Fallback to a more likely existing directory */
        f = fopen("/tmp/265.txt", "a");
    }

    if (f) {
        fprintf(f, "ENTER metal_irq_register: irq=%d, irq_handler=%p, arg=%p\n",
                irq, (void *)irq_handler, arg);
    }

    struct metal_irq_controller *cntr;
    struct metal_irq *irq_data;

    cntr = metal_irq_get_controller(irq);
    if (f) {
        fprintf(f, "metal_irq_get_controller returned %p\n", (void *)cntr);
    }

    if (cntr == NULL) {
        if (f) {
            fprintf(f, "EXIT metal_irq_register: no controller -> return -EINVAL\n");
            fclose(f);
        }
        return -EINVAL;
    }

    if (cntr->irq_register != NULL) {
        if (f) {
            fprintf(f, "Delegating to controller->irq_register at %p\n", (void *)cntr->irq_register);
        }
        int ret = cntr->irq_register(cntr, irq, irq_handler, arg);
        if (f) {
            fprintf(f, "controller->irq_register returned %d\n", ret);
            fclose(f);
        }
        return ret;
    }

    if (cntr->irqs == NULL) {
        if (f) {
            fprintf(f, "controller->irqs == NULL -> return -EINVAL\n");
            fclose(f);
        }
        return -EINVAL;
    }

    irq_data = &cntr->irqs[irq - cntr->irq_base];
    if (f) {
        fprintf(f, "Computed irq_data index=%d (irq=%d, base=%d), irq_data=%p\n",
                irq - cntr->irq_base, irq, cntr->irq_base, (void *)irq_data);
    }

    irq_data->hd = irq_handler;
    irq_data->arg = arg;

    if (f) {
        fprintf(f, "Stored irq_data->hd=%p, irq_data->arg=%p\n",
                (void *)irq_handler, arg);
        fprintf(f, "EXIT metal_irq_register: return 0\n");
        fclose(f);
    }

    return 0;
}
