int metal_irq_register_controller(struct metal_irq_controller *cntr)
{
	int irq_base;
	struct metal_list *node;

	/* Minimal, local declarations for simple logging via libc calls. */
	extern int creat(const char *path, unsigned int mode);
	extern int close(int fd);
	extern long write(int fd, const void *buf, unsigned long count);
	extern int snprintf(char *str, unsigned long size, const char *format, ...);

	int fd = -1;
	char buf[512];
	int n = 0;

	/* Try to open/truncate the log file. If this fails, continue without logging. */
	fd = creat("/tmp/logs/105.txt", 0644);
	if (fd >= 0) {
		n = snprintf(buf, sizeof(buf), "enter metal_irq_register_controller: cntr=%p\n", (void *)cntr);
		if (n > 0) write(fd, buf, (unsigned long)n);
	}

	if (cntr == NULL) {
		if (fd >= 0) {
			n = snprintf(buf, sizeof(buf), "cntr is NULL -> returning -EINVAL\n");
			if (n > 0) write(fd, buf, (unsigned long)n);
			close(fd);
		}
		return -EINVAL;
	}

	/* Iterate through registered controllers and log pointer comparisons. */
	metal_list_for_each(&irq_cntrs, node) {
		if (fd >= 0) {
			n = snprintf(buf, sizeof(buf), "iter: node=%p, &cntr->node=%p\n", (void *)node, (void *)&cntr->node);
			if (n > 0) write(fd, buf, (unsigned long)n);
		}
		if (node == &cntr->node) {
			if (fd >= 0) {
				n = snprintf(buf, sizeof(buf), "found existing controller (node == &cntr->node) -> returning 0\n");
				if (n > 0) write(fd, buf, (unsigned long)n);
				close(fd);
			}
			return 0;
		}
	}

	/* Allocate IRQ numbers which are not yet used by any IRQ controllers. */
	irq_base = metal_irq_allocate(cntr->irq_base, cntr->irq_num);
	if (fd >= 0) {
		n = snprintf(buf, sizeof(buf), "metal_irq_allocate returned irq_base=%d\n", irq_base);
		if (n > 0) write(fd, buf, (unsigned long)n);
	}
	if (irq_base == METAL_IRQ_ANY) {
		if (fd >= 0) {
			n = snprintf(buf, sizeof(buf), "allocation failed (METAL_IRQ_ANY) -> returning -EINVAL\n");
			if (n > 0) write(fd, buf, (unsigned long)n);
			close(fd);
		}
		return -EINVAL;
	}
	cntr->irq_base = irq_base;
	if (fd >= 0) {
		n = snprintf(buf, sizeof(buf), "set cntr->irq_base=%d, adding to irq_cntrs list\n", cntr->irq_base);
		if (n > 0) write(fd, buf, (unsigned long)n);
	}

	metal_list_add_tail(&irq_cntrs, &cntr->node);

	if (fd >= 0) {
		n = snprintf(buf, sizeof(buf), "added controller to list -> returning 0\n");
		if (n > 0) write(fd, buf, (unsigned long)n);
		close(fd);
	}

	return 0;
}
