int metal_irq_register_controller(struct metal_irq_controller *cntr)
{
    FILE *f = NULL;
    int irq_base;
    struct metal_list *node;

    /* Try to open log file (best-effort). If it fails, continue without logging. */
    f = fopen("/tmp/logs/105.txt", "a");
    if (f) {
        fprintf(f, "ENTER: metal_irq_register_controller cntr=%p\n", (void *)cntr);
        fprintf(f, "NOTE: iterator declared as 'struct metal_list *node' (non-const)\n");
    }

    if (cntr == NULL) {
        if (f) {
            fprintf(f, "EXIT: cntr == NULL -> return -EINVAL\n");
            fclose(f);
        }
        return -EINVAL;
    }

    /* Traverse list and log each visited node to capture traversal behavior. */
    metal_list_for_each(&irq_cntrs, node) {
        if (f) {
            /* Log the iterator pointer value and the address of cntr->node for comparison evidence. */
            fprintf(f, "LOOP: iter node=%p, cntr->node=%p\n", (void *)node, (void *)&cntr->node);
        }
        if (node == &cntr->node) {
            if (f) {
                fprintf(f, "FOUND: node == &cntr->node -> already registered, return 0\n");
                fclose(f);
            }
            return 0;
        }
    }

    /* Allocate IRQ numbers which are not yet used by any IRQ controllers. */
    if (f) fprintf(f, "ACTION: calling metal_irq_allocate(cntr->irq_base=%d, cntr->irq_num=%d)\n",
                    cntr->irq_base, cntr->irq_num);
    irq_base = metal_irq_allocate(cntr->irq_base, cntr->irq_num);
    if (f) fprintf(f, "RESULT: metal_irq_allocate returned irq_base=%d\n", irq_base);
    if (irq_base == METAL_IRQ_ANY) {
        if (f) {
            fprintf(f, "EXIT: irq_base == METAL_IRQ_ANY -> return -EINVAL\n");
            fclose(f);
        }
        return -EINVAL;
    }
    cntr->irq_base = irq_base;

    if (f) fprintf(f, "ACTION: metal_list_add_tail(&irq_cntrs, &cntr->node) (adding node=%p)\n",
                    (void *)&cntr->node);
    metal_list_add_tail(&irq_cntrs, &cntr->node);

    if (f) {
        fprintf(f, "EXIT: success -> return 0 (assigned irq_base=%d)\n", cntr->irq_base);
        fclose(f);
    }

    return 0;
}
