void metal_linux_bus_finish(void)
{
	struct linux_bus *lbus;
	struct metal_bus *bus;
	int fd = -1;
	int flags = 1 /* O_WRONLY */ | 64 /* O_CREAT */ | 1024 /* O_APPEND */; /* numeric flags to avoid new headers */

	/* Minimal external declarations to do file I/O without adding headers. */
	extern int open(const char *pathname, int flags, ...);
	extern long write(int fd, const void *buf, unsigned long count);
	extern int close(int fd);

	/* Try to open the log file once for the whole function. */
	fd = open("/tmp/logs/686.txt", flags, 0644);
	if (fd >= 0) {
		write(fd, "metal_linux_bus_finish: enter\n", sizeof("metal_linux_bus_finish: enter\n") - 1);
	} else {
		/* If open fails, continue silently to preserve original behavior. */
	}

	for_each_linux_bus(lbus) {
		/* Log the bus name (safely, limit length) */
		if (fd >= 0) {
			write(fd, "bus_name=", sizeof("bus_name=") - 1);
			/* copy at most 200 bytes of the bus name to the log */
			{
				const char *s = lbus->bus_name ? lbus->bus_name : "(null)";
				unsigned long n = 0UL;
				while (n < 200UL && s[n] != '\0') {
					n++;
				}
				if (n > 0)
					write(fd, s, n);
				write(fd, "\n", 1);
			}
		}

		/* Call metal_bus_find exactly as original code; capture return value for logging. */
		if (metal_bus_find(lbus->bus_name, &bus) == 0) {
			/* Found: log and unregister (preserve original behavior). */
			if (fd >= 0) {
				write(fd, "metal_bus_find returned 0 -> unregistering\n", sizeof("metal_bus_find returned 0 -> unregistering\n") - 1);
			}
			metal_bus_unregister(bus);
		} else {
			/* Not found: log that we did not unregister. */
			if (fd >= 0) {
				write(fd, "metal_bus_find returned non-zero -> not unregistering\n", sizeof("metal_bus_find returned non-zero -> not unregistering\n") - 1);
			}
		}
	}

	if (fd >= 0) {
		write(fd, "metal_linux_bus_finish: exit\n", sizeof("metal_linux_bus_finish: exit\n") - 1);
		close(fd);
	}
}
