void metal_linux_bus_finish(void)
{
	/* Ensure required prototypes are available for logging helpers. */
	#include <stdio.h>
	#include <sys/stat.h>
	#include <sys/types.h>

	/* Attempt to create log directory; ignore errors if it already exists. */
	(void)mkdir("/tmp/logs", 0755);

	FILE *log = fopen("/tmp/logs/685.txt", "a");
	if (log) {
		fprintf(log, "Entered metal_linux_bus_finish\n");
	}

	struct linux_bus *lbus;
	struct metal_bus *bus;

	for_each_linux_bus(lbus) {
		/* Log the current linux_bus pointer and its bus_name (if available). */
		if (log) {
			const char *name = (lbus && lbus->bus_name) ? lbus->bus_name : "(null)";
			fprintf(log, "Iter: lbus=%p bus_name=%s\n", (void *)lbus, name);
		}

		if (metal_bus_find(lbus->bus_name, &bus) == 0) {
			if (log) {
				fprintf(log, "metal_bus_find succeeded for name=%s, metal_bus=%p -- calling metal_bus_unregister\n",
					(lbus && lbus->bus_name) ? lbus->bus_name : "(null)", (void *)bus);
			}
			metal_bus_unregister(bus);
		} else {
			if (log) {
				fprintf(log, "metal_bus_find found no bus for name=%s\n",
					(lbus && lbus->bus_name) ? lbus->bus_name : "(null)");
			}
		}
	}

	if (log) {
		fprintf(log, "Exiting metal_linux_bus_finish\n");
		fclose(log);
	}
}
