void metal_linux_bus_finish(void)
{
	struct linux_bus *lbus;
	struct metal_bus *bus;
	FILE *log = fopen("/tmp/logs/685.txt", "a");
	if (log) {
		fprintf(log, "ENTER metal_linux_bus_finish\n");
		fflush(log);
	}

	for_each_linux_bus(lbus) {
		int found = -1;
		const char *name = "(null)";
		if (lbus && lbus->bus_name)
			name = lbus->bus_name;

		if (log)
			fprintf(log, "ITER: lbus=%p bus_name=%s\n", (void *)lbus, name);

		/* preserve original behavior */
		found = metal_bus_find(lbus->bus_name, &bus);

		if (log)
			fprintf(log, "metal_bus_find returned %d for bus_name=%s, bus=%p\n", found, name, (void *)bus);

		if (found == 0) {
			if (log)
				fprintf(log, "Calling metal_bus_unregister on bus=%p\n", (void *)bus);
			metal_bus_unregister(bus);
			if (log)
				fprintf(log, "Returned from metal_bus_unregister for bus=%p\n", (void *)bus);
		}

		if (log)
			fflush(log);
	}

	if (log) {
		fprintf(log, "EXIT metal_linux_bus_finish\n");
	 fclose(log);
	}
}
