int metal_linux_bus_init(void)
{
	struct linux_bus *lbus;
	int valid = 0;
	FILE *logf = fopen("/tmp/logs/578.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER: metal_linux_bus_init\n");
	}

	for_each_linux_bus(lbus) {
		/* Call probe exactly once per bus as original code did. */
		int probe_ok = metal_linux_probe_bus(lbus);
		int contrib = probe_ok ? 0 : 1; /* same as original: add 0 on success, 1 on failure */

		if (logf) {
			fprintf(logf, "PROBE: bus=%p probe_ok=%d contrib=%d\n", (void *)lbus, probe_ok, contrib);
		}

		valid += contrib;
	}

	if (logf) {
		fprintf(logf, "EXIT: metal_linux_bus_init valid=%d return=%d\n", valid, valid ? 0 : -ENODEV);
		fclose(logf);
	}

	return valid ? 0 : -ENODEV;
}
