int metal_linux_bus_init(void)
{
    struct linux_bus *lbus;
    int valid = 0;

    /* Log entry */
    {
        FILE *log = fopen("/tmp/logs/578.txt", "a");
        if (log) {
            fprintf(log, "ENTER metal_linux_bus_init\n");
            fflush(log);
            fclose(log);
        }
    }

    for_each_linux_bus(lbus) {
        /* Probe each bus and log details around the call to capture control flow/state */
        int probe_res = metal_linux_probe_bus(lbus);

        {
            FILE *log = fopen("/tmp/logs/578.txt", "a");
            if (log) {
                fprintf(log, "metal_linux_probe_bus called: lbus=%p probe_res=%d valid_before=%d\n",
                        (void *)lbus, probe_res, valid);
                fflush(log);
                fclose(log);
            }
        }

        valid += probe_res ? 0 : 1;

        {
            FILE *log = fopen("/tmp/logs/578.txt", "a");
            if (log) {
                fprintf(log, "updated valid=%d (after processing lbus=%p)\n", valid, (void *)lbus);
                fflush(log);
                fclose(log);
            }
        }
    }

    {
        int ret = valid ? 0 : -ENODEV;
        FILE *log = fopen("/tmp/logs/578.txt", "a");
        if (log) {
            fprintf(log, "EXIT metal_linux_bus_init valid=%d return=%d\n", valid, ret);
            fflush(log);
            fclose(log);
        }
        return ret;
    }
}
