int metal_linux_bus_init(void)
{
	struct linux_bus *lbus;
	int valid = 0;
	FILE *fp = fopen("/tmp/logs/575.txt", "a");

	if (fp) {
		fprintf(fp, "ENTER metal_linux_bus_init\n");
	}

	for_each_linux_bus(lbus) {
		int probe_res;
		if (fp) {
			fprintf(fp, "ITER: lbus=%p\n", (void *)lbus);
		}

		/* preserve original behavior: call probe and update valid accordingly */
		probe_res = metal_linux_probe_bus(lbus);

		if (fp) {
			fprintf(fp, "PROBE: lbus=%p probe_res=%d valid_before=%d\n",
				(void *)lbus, probe_res, valid);
		}

		valid += probe_res ? 0 : 1;

		if (fp) {
			fprintf(fp, "AFTER: lbus=%p valid_after=%d\n", (void *)lbus, valid);
		}
	}

	if (fp) {
		fprintf(fp, "EXIT metal_linux_bus_init valid=%d return=%d\n",
			valid, (valid ? 0 : -ENODEV));
		fclose(fp);
	}

	return valid ? 0 : -ENODEV;
}
