int metal_linux_bus_init(void)
{
	struct linux_bus *lbus;
	int valid = 0;
	int iter = 0;
	/* Log that we entered the function */
	{
		FILE *f = fopen("/tmp/logs/575.txt", "a");
		if (f) {
			fprintf(f, "metal_linux_bus_init: entered\n");
			fclose(f);
		}
	}

	for_each_linux_bus(lbus) {
		/* Preserve original behavior while capturing state per-iteration. */
		int probe_ok = metal_linux_probe_bus(lbus) ? 0 : 1;
		valid += probe_ok;

		/* Per-iteration logging: iteration index, pointer value, probe result. */
		{
			FILE *f = fopen("/tmp/logs/575.txt", "a");
			if (f) {
				fprintf(f, "iteration=%d, lbus_ptr=%p, probe_ok=%d\n", iter, (void *)lbus, probe_ok);
				fclose(f);
			}
		}

		iter++;
	}

	/* Log final outcome */
	{
		FILE *f = fopen("/tmp/logs/575.txt", "a");
		if (f) {
			fprintf(f, "metal_linux_bus_init: exiting, valid=%d, return=%d\n", valid, (valid ? 0 : -ENODEV));
			fclose(f);
		}
	}

	return valid ? 0 : -ENODEV;
}
