int metal_linux_bus_init(void)
{
    FILE *log = fopen("/tmp/logs/575.txt", "a");
    if (log)
        fprintf(log, "metal_linux_bus_init: entered\n");

    struct linux_bus *lbus;
    int valid = 0;
    int __iter_count = 0;

    for_each_linux_bus(lbus) {
        if (log)
            fprintf(log, "iter=%d: lbus=%p\n", __iter_count, (void *)lbus);

        /* Call probe once, preserve original semantics */
        int __probe_res = metal_linux_probe_bus(lbus);

        if (log)
            fprintf(log, "iter=%d: metal_linux_probe_bus(lbus=%p) returned=%d\n",
                    __iter_count, (void *)lbus, __probe_res);

        valid += __probe_res ? 0 : 1;
        __iter_count++;
    }

    if (log) {
        fprintf(log, "metal_linux_bus_init: exiting valid=%d\n", valid);
        fclose(log);
    }

    return valid ? 0 : -ENODEV;
}
