int metal_linux_bus_init(void)
{
	struct linux_bus *lbus;
	int valid = 0;
	/* Attempt to record control flow/state for debugging the pointer-const scenario. */
	/* Logging is best-effort: if the file can't be opened we still preserve original logic. */
	FILE *log = fopen("/tmp/logs/575.txt", "a");
	if (log) {
		fprintf(log, "metal_linux_bus_init: enter\n");
	}

	for_each_linux_bus(lbus) {
		/* Log the loop entry and the pointer value. Avoid dereferencing unknown structure members. */
		if (log) {
			fprintf(log, "for_each_linux_bus: iteration, lbus=%p\n", (void *)lbus);
		}

		/* Preserve original logic: evaluate probe once and update `valid` accordingly. */
		int probe_ok = metal_linux_probe_bus(lbus);
		valid += probe_ok ? 0 : 1;

		if (log) {
			fprintf(log, "metal_linux_probe_bus returned %d; accumulated valid=%d\n", probe_ok, valid);
		}
	}

	if (log) {
		int ret = valid ? 0 : -ENODEV;
		fprintf(log, "metal_linux_bus_init: exit, valid=%d, return=%d\n", valid, ret);
		fclose(log);
	}

	return valid ? 0 : -ENODEV;
}
