static void *metal_linux_irq_handling(void *args)
{
	struct sched_param param;
	uint64_t val;
	int ret;
	int i, j, pfds_total;
	struct pollfd *pfds;
	FILE *logf = NULL;

	(void)args;

	/* Open the log file once for the lifetime of this thread/function. */
	logf = fopen("/tmp/logs/248.txt", "a");
	if (logf) {
		fprintf(logf, "%s: entered metal_linux_irq_handling\n", __func__);
		fflush(logf);
	}

	pfds = (struct pollfd *)malloc(FD_SETSIZE * sizeof(struct pollfd));
	if (!pfds) {
		metal_log(METAL_LOG_ERROR,
			  "%s: failed to allocate irq fds mem.\n", __func__);
		if (logf) {
			fprintf(logf, "%s: malloc failed for %zu bytes\n", __func__,
				FD_SETSIZE * sizeof(struct pollfd));
			fflush(logf);
		}
		if (logf)
			fclose(logf);
		return NULL;
	}

	if (logf) {
		fprintf(logf, "%s: allocated pfds at %p (bytes=%zu), memset_bytes=%zu (MAX_IRQS * sizeof)\n",
			__func__, (void *)pfds,
			(FD_SETSIZE * sizeof(struct pollfd)),
			(MAX_IRQS * sizeof(struct pollfd)));
		fflush(logf);
	}

	param.sched_priority = sched_get_priority_max(SCHED_FIFO);
	/* Ignore the set scheduler error */
	ret = sched_setscheduler(0, SCHED_FIFO, &param);
	if (ret) {
		metal_log(METAL_LOG_WARNING,
			  "%s: Failed to set scheduler: %s.\n", __func__,
			  strerror(ret));
		if (logf) {
			fprintf(logf, "%s: sched_setscheduler returned %d\n", __func__, ret);
			fflush(logf);
		}
	}

	while (1) {
		metal_mutex_acquire(&irq_lock);
		if (irq_handling_stop) {
			/* Killing this IRQ handling thread */
			metal_mutex_release(&irq_lock);
			break;
		}

		/* Get the fdset */
		if (logf) {
			fprintf(logf, "%s: about to memset pfds (%p) with %zu bytes\n",
				__func__, (void *)pfds, (size_t)(MAX_IRQS * sizeof(struct pollfd)));
			fflush(logf);
		}
		memset(pfds, 0, MAX_IRQS * sizeof(struct pollfd));

		/* Record that we explicitly set pfds[0] and subsequent entries. */
		pfds[0].fd = irq_notify_fd;
		pfds[0].events = POLLIN;
		j = 1;
		if (logf) {
			fprintf(logf, "%s: set pfds[0] -> fd=%d, events=0x%x, j=%d\n",
				__func__, pfds[0].fd, pfds[0].events, j);
			fflush(logf);
		}

		metal_bitmap_for_each_set_bit(irqs_enabled, i,
					      linux_irq_cntr.irq_num) {
			pfds[j].fd = i;
			pfds[j].events = POLLIN;
			if (logf) {
				fprintf(logf, "%s: bitmap set bit -> pfds[%d].fd=%d, events=0x%x\n",
					__func__, j, pfds[j].fd, pfds[j].events);
				fflush(logf);
			}
			j++;
		}

		/* Log the number of fds that will be passed to poll and list them. */
		if (logf) {
			fprintf(logf, "%s: after init, nfds(j)=%d. Listing pfds[0..j-1]:\n",
				__func__, j);
			for (int _k = 0; _k < j; _k++) {
				fprintf(logf, "  pfds[%d]: fd=%d, events=0x%x, revents=0x%x\n",
					_k, pfds[_k].fd, pfds[_k].events, pfds[_k].revents);
			}
			fflush(logf);
		}

		metal_mutex_release(&irq_lock);
		/* Wait for interrupt */
		ret = poll(pfds, j, -1);
		if (ret < 0) {
			metal_log(METAL_LOG_ERROR, "%s: poll() failed: %s.\n",
				  __func__, strerror(errno));
			if (logf) {
				fprintf(logf, "%s: poll() failed, ret=%d, errno=%d (%s)\n",
					__func__, ret, errno, strerror(errno));
				fflush(logf);
			}
			break;
		}
		/* Waken up from interrupt */
		pfds_total = j;
		if (logf) {
			fprintf(logf, "%s: poll returned %d, pfds_total=%d\n",
				__func__, ret, pfds_total);
			fflush(logf);
		}
		for (i = 0; i < pfds_total; i++) {
			if (logf) {
				fprintf(logf, "%s: checking pfds[%d] fd=%d revents=0x%x\n",
					__func__, i, pfds[i].fd, pfds[i].revents);
				fflush(logf);
			}
			if ((pfds[i].fd == irq_notify_fd) &&
			    (pfds[i].revents & (POLLIN | POLLRDNORM))) {
				/* IRQ registration change notification */
				if (read(pfds[i].fd,
					 (void *)&val, sizeof(uint64_t)) < 0) {
						metal_log(METAL_LOG_ERROR,
						  "%s, read irq fd %d failed\n",
						  __func__, pfds[i].fd);
						if (logf) {
							fprintf(logf, "%s: read on irq_notify_fd %d failed\n",
							__func__, pfds[i].fd);
							fflush(logf);
						}
				}
			} else if ((pfds[i].revents & (POLLIN | POLLRDNORM))) {
				struct metal_device *dev = NULL;
				int irq_handled = 0;
				int fd;

				fd = pfds[i].fd;
				dev = irqs_devs[fd];
				metal_mutex_acquire(&irq_lock);
				if (metal_irq_handle(&irqs[fd], fd)
				    == METAL_IRQ_HANDLED)
					irq_handled = 1;
				if (irq_handled) {
					if (dev && dev->bus->ops.dev_irq_ack)
						dev->bus->ops.dev_irq_ack(
							dev->bus, dev, fd);
				}
				metal_mutex_release(&irq_lock);
				if (logf) {
					fprintf(logf, "%s: handled irq on fd=%d, irq_handled=%d\n",
						__func__, fd, irq_handled);
					fflush(logf);
				}
			} else if (pfds[i].revents) {
				metal_log(METAL_LOG_DEBUG,
					  "%s: poll unexpected. fd %d: %d\n",
					  __func__,
					  pfds[i].fd, pfds[i].revents);
				if (logf) {
					fprintf(logf, "%s: poll unexpected. fd=%d revents=0x%x\n",
						__func__, pfds[i].fd, pfds[i].revents);
					fflush(logf);
				}
			}
		}
	}
	free(pfds);
	if (logf) {
		fprintf(logf, "%s: exiting, freed pfds at %p\n", __func__, (void *)pfds);
		fflush(logf);
		fclose(logf);
	}
	return NULL;
}
