int metal_linux_irq_init(void)
{
	int ret;
	FILE *log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "ENTER: metal_linux_irq_init\n");
		fclose(log);
	}

	memset(&irqs, 0, sizeof(irqs));

	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "AFTER memset: irqs_addr=%p size=%lu\n", (void *)&irqs, (unsigned long)sizeof(irqs));
		fclose(log);
	}

	irq_notify_fd = eventfd(0, EFD_CLOEXEC);
	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "AFTER eventfd: irq_notify_fd=%d errno=%d\n", irq_notify_fd, errno);
		fclose(log);
	}
	if (irq_notify_fd < 0) {
		/* Log that metal_log will be invoked for the error branch so that
		   evidence of that path is present in the file for later inspection. */
		log = fopen("/tmp/logs/547.txt", "a");
		if (log) {
			fprintf(log, "ERROR PATH: eventfd failed, about to call metal_log(METAL_LOG_ERROR, ...)\n");
			fclose(log);
		}

		metal_log(METAL_LOG_ERROR,
			  "Failed to create eventfd for IRQ handling.\n");
		return  -EAGAIN;
	}

	metal_mutex_init(&irq_lock);
	irq_handling_stop = false;
	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "AFTER mutex init: irq_handling_stop=%d\n", (int)irq_handling_stop);
		fclose(log);
	}

	ret = metal_irq_register_controller(&linux_irq_cntr);
	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "AFTER metal_irq_register_controller: ret=%d\n", ret);
		fclose(log);
	}
	if (ret < 0) {
		log = fopen("/tmp/logs/547.txt", "a");
		if (log) {
			fprintf(log, "ERROR PATH: irq controller registration failed, about to call metal_log(METAL_LOG_ERROR, ...)\n");
			fclose(log);
		}

		metal_log(METAL_LOG_ERROR,
			  "Linux IRQ controller failed to register.\n");
		return -EINVAL;
	}

	ret = pthread_create(&irq_pthread, NULL,
			     metal_linux_irq_handling, NULL);
	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "AFTER pthread_create: ret=%d\n", ret);
		fclose(log);
	}
	if (ret != 0) {
		log = fopen("/tmp/logs/547.txt", "a");
		if (log) {
			fprintf(log, "ERROR PATH: pthread_create failed (%d), about to call metal_log(METAL_LOG_ERROR, ...)\n", ret);
			fclose(log);
		}

		metal_log(METAL_LOG_ERROR, "Failed to create IRQ thread: %d.\n",
			  ret);
		return -EAGAIN;
	}

	log = fopen("/tmp/logs/547.txt", "a");
	if (log) {
		fprintf(log, "EXIT: metal_linux_irq_init -> success (0)\n");
		fclose(log);
	}

	return 0;
}
