int metal_linux_irq_init(void)
{
	int ret;
	FILE *f = fopen("/tmp/logs/548.txt", "a");

	/* Log entry into the function */
	if (f) {
		fprintf(f, "ENTER metal_linux_irq_init\n");
#ifdef MAX_IRQS
		fprintf(f, "MAX_IRQS=%d\n", (int)MAX_IRQS);
#else
		fprintf(f, "MAX_IRQS=UNDEF\n");
#endif
#ifdef FD_SETSIZE
		fprintf(f, "FD_SETSIZE=%d\n", (int)FD_SETSIZE);
#else
		fprintf(f, "FD_SETSIZE=UNDEF\n");
#endif
		fflush(f);
	}

	/* Preserve original initialization */
	memset(&irqs, 0, sizeof(irqs));
	if (f) {
		fprintf(f, "memset(&irqs,0,%zu) called\n", sizeof(irqs));
		fflush(f);
	}

	irq_notify_fd = eventfd(0, EFD_CLOEXEC);
	if (f) {
		fprintf(f, "eventfd returned irq_notify_fd=%d\n", irq_notify_fd);
		fflush(f);
	}
	if (irq_notify_fd < 0) {
		if (f) {
			fprintf(f, "eventfd failed with errno=%d\n", errno);
			fflush(f);
			fclose(f);
		}
		metal_log(METAL_LOG_ERROR,
			  "Failed to create eventfd for IRQ handling.\n");
		return  -EAGAIN;
	}

	metal_mutex_init(&irq_lock);
	irq_handling_stop = false;
	if (f) {
		fprintf(f, "irq_lock initialized; irq_handling_stop=false\n");
		fflush(f);
	}

	ret = metal_irq_register_controller(&linux_irq_cntr);
	if (f) {
		fprintf(f, "metal_irq_register_controller returned %d\n", ret);
		fflush(f);
	}
	if (ret < 0) {
		if (f) {
			fclose(f);
		}
		metal_log(METAL_LOG_ERROR,
			  "Linux IRQ controller failed to register.\n");
		return -EINVAL;
	}

	ret = pthread_create(&irq_pthread, NULL,
			     metal_linux_irq_handling, NULL);
	if (f) {
		fprintf(f, "pthread_create returned %d\n", ret);
		fflush(f);
		fclose(f);
	}
	if (ret != 0) {
		metal_log(METAL_LOG_ERROR, "Failed to create IRQ thread: %d.\n",
			  ret);
		return -EAGAIN;
	}

	if (f == NULL) {
		/* If we couldn't open the log earlier, write a minimal trace via metal_log */
		metal_log(METAL_LOG_DEBUG, "metal_linux_irq_init: completed (no file log)\n");
	}

	return 0;
}
