int metal_linux_irq_init(void)
{
    int ret;
    FILE *log = fopen("/tmp/logs/545.txt", "a");

    if (log) {
        fprintf(log, "ENTER: metal_linux_irq_init\n");
        fflush(log);
    }

    memset(&irqs, 0, sizeof(irqs));
    if (log) {
        fprintf(log, "after memset(&irqs)\n");
        fflush(log);
    }

    irq_notify_fd = eventfd(0, EFD_CLOEXEC);
    if (log) {
        fprintf(log, "eventfd() returned: %d\n", irq_notify_fd);
        fflush(log);
    }

    if (irq_notify_fd < 0) {
        if (log) {
            fprintf(log, "ERROR: eventfd failed (fd=%d)\n", irq_notify_fd);
            fclose(log);
        }
        metal_log(METAL_LOG_ERROR,
                  "Failed to create eventfd for IRQ handling.\n");
        return  -EAGAIN;
    }

    metal_mutex_init(&irq_lock);
    if (log) {
        fprintf(log, "metal_mutex_init(&irq_lock) done\n");
        fflush(log);
    }

    irq_handling_stop = false;
    if (log) {
        fprintf(log, "irq_handling_stop set to false\n");
        fflush(log);
    }

    /* Log the controller pointer address to help reproduce the 8.13 scenario. */
    if (log) {
        fprintf(log, "about to call metal_irq_register_controller(&linux_irq_cntr=%p)\n", (void *)&linux_irq_cntr);
        fflush(log);
    }

    ret = metal_irq_register_controller(&linux_irq_cntr);
    if (log) {
        fprintf(log, "metal_irq_register_controller() returned: %d\n", ret);
        fflush(log);
    }

    if (ret < 0) {
        if (log) {
            fprintf(log, "ERROR: linux IRQ controller failed to register (ret=%d)\n", ret);
            fclose(log);
        }
        metal_log(METAL_LOG_ERROR,
                  "Linux IRQ controller failed to register.\n");
        return -EINVAL;
    }

    ret = pthread_create(&irq_pthread, NULL,
                         metal_linux_irq_handling, NULL);
    if (log) {
        fprintf(log, "pthread_create returned: %d\n", ret);
        fflush(log);
    }

    if (ret != 0) {
        if (log) {
            fprintf(log, "ERROR: Failed to create IRQ thread: %d\n", ret);
            fclose(log);
        }
        metal_log(METAL_LOG_ERROR, "Failed to create IRQ thread: %d.\n",
                  ret);
        return -EAGAIN;
    }

    if (log) {
        fprintf(log, "EXIT: metal_linux_irq_init success (return 0)\n");
        fclose(log);
    }

    return 0;
}
