int metal_linux_irq_init(void)
{
	int ret;
	FILE *logf = NULL;

	/* Open log file to capture control flow and state for debugging. */
	logf = fopen("/tmp/logs/545.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER: metal_linux_irq_init()\n");
	}

	memset(&irqs, 0, sizeof(irqs));
	if (logf) {
		fprintf(logf, "STATE: irqs zero-initialized\n");
	}

	irq_notify_fd = eventfd(0, EFD_CLOEXEC);
	if (logf) {
		fprintf(logf, "CALL: eventfd -> irq_notify_fd=%d\n", irq_notify_fd);
	}

	if (irq_notify_fd < 0) {
		if (logf) {
			fprintf(logf, "ERROR: eventfd failed (irq_notify_fd=%d), returning -EAGAIN\n", irq_notify_fd);
			fclose(logf);
		}
		metal_log(METAL_LOG_ERROR,
			  "Failed to create eventfd for IRQ handling.\n");
		return  -EAGAIN;
	}

	metal_mutex_init(&irq_lock);
	irq_handling_stop = false;
	if (logf) {
		fprintf(logf, "STATE: irq_lock initialized, irq_handling_stop=false\n");
	}

	/* Log details of the controller being registered. This reads fields that
	 * are only used for inspection here (irq_base, irq_num) so that we can
	 * capture evidence that the controller is read-only in this path.
	 */
	if (logf) {
		/* Try to print common controller fields if present. This is useful to
		 * reproduce the scenario where controller data is only read.
		 */
		/* Be conservative: use a single fprintf to avoid partial writes. */
		fprintf(logf, "CALL: metal_irq_register_controller(&linux_irq_cntr)\n");
		/* Attempt to print irq_base and irq_num if available in the struct. */
		/* These members are expected by the diagnostic that reported the violation. */
		fprintf(logf, "CONTROLLER: linux_irq_cntr.irq_base=%d, linux_irq_cntr.irq_num=%d\n",
				(int)linux_irq_cntr.irq_base, (int)linux_irq_cntr.irq_num);
	}

	ret = metal_irq_register_controller(&linux_irq_cntr);
	if (logf) {
		fprintf(logf, "RETURN: metal_irq_register_controller -> ret=%d\n", ret);
	}
	if (ret < 0) {
		if (logf) {
			fprintf(logf, "ERROR: Linux IRQ controller failed to register, returning -EINVAL\n");
			fclose(logf);
		}
		metal_log(METAL_LOG_ERROR,
			  "Linux IRQ controller failed to register.\n");
		return -EINVAL;
	}

	ret = pthread_create(&irq_pthread, NULL,
			     metal_linux_irq_handling, NULL);
	if (logf) {
		fprintf(logf, "CALL: pthread_create -> ret=%d\n", ret);
	}
	if (ret != 0) {
		if (logf) {
			fprintf(logf, "ERROR: Failed to create IRQ thread: %d, returning -EAGAIN\n", ret);
			fclose(logf);
		}
		metal_log(METAL_LOG_ERROR, "Failed to create IRQ thread: %d.\n",
			  ret);
		return -EAGAIN;
	}

	if (logf) {
		fprintf(logf, "EXIT: metal_linux_irq_init() -> success (0)\n");
		fclose(logf);
	}

	return 0;
}
