static int metal_linux_irq_notify(void)
{
	uint64_t val = 1;
	int ret;

	/* Open a persistent log so callers can inspect control flow and state. */
	FILE *logf = fopen("/tmp/logs/557.txt", "a");
	if (logf) {
		fprintf(logf, "metal_linux_irq_notify: enter; irq_notify_fd=%d; val=%llu\n", irq_notify_fd, (unsigned long long)val);
		fflush(logf);
	}

	ret = write(irq_notify_fd, &val, sizeof(val));
	if (ret < 0) {
		int saved_errno = errno;
		/* Preserve original behavior: call metal_log as before. This call is the
		   place other tools/agents can inspect (the macro expansion may reveal
		   the compiler-extension usage reported in the violation). */
		metal_log(METAL_LOG_ERROR, "%s failed\n", __func__);

		if (logf) {
			fprintf(logf, "metal_linux_irq_notify: write failed; ret=%d; errno=%d (%s)\n",
				ret, saved_errno, strerror(saved_errno));
		}
	} else {
		if (logf) {
			fprintf(logf, "metal_linux_irq_notify: write succeeded; ret=%d\n", ret);
		}
	}

	if (logf) {
		fflush(logf);
		fclose(logf);
	}

	return ret;
}
