void metal_linux_irq_shutdown(void)
{
	int ret;
	int log_fd = -1;
	char buf[256];
	int len;

	/* Try to open the log file for appending. If it fails, proceed silently. */
	log_fd = open("/tmp/logs/677.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "ENTER %s\n", __func__);
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	/* Original debug log call (keeps original behavior). */
	metal_log(METAL_LOG_DEBUG, "%s\n", __func__);
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Called metal_log DEBUG\n");
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	/* Preserve original logic: mark handling stop and notify. */
	irq_handling_stop = true;
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Set irq_handling_stop = %d\n", irq_handling_stop ? 1 : 0);
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	metal_linux_irq_notify();
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Called metal_linux_irq_notify()\n");
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	/* Join the IRQ thread as before and log the return value. */
	ret = pthread_join(irq_pthread, NULL);
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "pthread_join returned: %d\n", ret);
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	if (ret) {
		/* Preserve original error logging behavior. */
		metal_log(METAL_LOG_ERROR, "Failed to join IRQ thread: %d.\n",
			  ret);
		if (log_fd >= 0) {
			len = snprintf(buf, sizeof(buf), "ERROR: Failed to join IRQ thread: %d\n", ret);
			if (len > 0)
				write(log_fd, buf, (size_t)len);
		}
	}

	/* Log about closing the notify fd and then perform original close. */
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Closing irq_notify_fd = %d\n", irq_notify_fd);
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	close(irq_notify_fd);
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Closed irq_notify_fd\n");
		if (len > 0)
			write(log_fd, buf, (size_t)len);
	}

	/* Deinitialize the mutex as in the original function. */
	metal_mutex_deinit(&irq_lock);
	if (log_fd >= 0) {
		len = snprintf(buf, sizeof(buf), "Deinitialized irq_lock and exiting %s\n", __func__);
		if (len > 0)
			write(log_fd, buf, (size_t)len);
		close(log_fd);
	}
}
