void metal_linux_irq_shutdown(void)
{
	int ret;
	FILE *log = fopen("/tmp/logs/677.txt", "a");

	if (log) {
		fprintf(log, "ENTER: %s\n", __func__);
		fprintf(log, "state_before: irq_handling_stop=%d, irq_notify_fd=%d\n",
			(int)irq_handling_stop, irq_notify_fd);
#ifdef __GNUC__
		fprintf(log, "compiler_probe: __GNUC__ defined -> version %d.%d\n",
			__GNUC__, __GNUC_MINOR__);
		fprintf(log, "note: presence of GCC implies support for GCC-specific extensions such as statement-expressions '({ ... })' and the ',##__VA_ARGS__' comma-swallowing extension.\n");
#else
		fprintf(log, "compiler_probe: __GNUC__ not defined.\n");
#endif
	}

	metal_log(METAL_LOG_DEBUG, "%s\n", __func__);
	irq_handling_stop = true;
	if (log)
		fprintf(log, "action: set irq_handling_stop=%d\n", (int)irq_handling_stop);

	metal_linux_irq_notify();
	if (log)
		fprintf(log, "action: called metal_linux_irq_notify()\n");

	ret = pthread_join(irq_pthread, NULL);
	if (ret) {
		metal_log(METAL_LOG_ERROR, "Failed to join IRQ thread: %d.\n",
			  ret);
		if (log)
			fprintf(log, "error: pthread_join returned %d\n", ret);
	} else {
		if (log)
			fprintf(log, "info: pthread_join succeeded (ret=%d)\n", ret);
	}

	close(irq_notify_fd);
	if (log)
		fprintf(log, "action: closed irq_notify_fd=%d\n", irq_notify_fd);

	metal_mutex_deinit(&irq_lock);
	if (log) {
		fprintf(log, "action: metal_mutex_deinit(&irq_lock) done\n");
		fprintf(log, "EXIT: %s\n\n", __func__);
		fclose(log);
	}
}
