static int metal_linux_probe_bus(struct linux_bus *lbus)
{
	struct linux_driver *ldrv;
	int ret, error = -ENODEV;
	FILE *logf = NULL;

	/* Open log file for append to record control flow and state. */
	logf = fopen("/tmp/logs/228.txt", "a");
	if (logf) {
		fprintf(logf, "metal_linux_probe_bus: entered (lbus=%p)\n", (void *)lbus);
		if (lbus)
			fprintf(logf, "  bus_name=%p\n", (void *)lbus->bus_name);
		else
			fprintf(logf, "  lbus is NULL\n");
		fclose(logf);
	}

	lbus->sbus = sysfs_open_bus(lbus->bus_name);
	logf = fopen("/tmp/logs/228.txt", "a");
	if (logf) {
		if (!lbus->sbus)
			fprintf(logf, "  sysfs_open_bus(%p) returned NULL -> returning -ENODEV\n", (void *)lbus->bus_name);
		else
			fprintf(logf, "  sysfs_open_bus(%p) succeeded (sbus=%p)\n", (void *)lbus->bus_name, (void *)lbus->sbus);
		fclose(logf);
	}
	if (!lbus->sbus)
		return -ENODEV;

	for_each_linux_driver(lbus, ldrv) {
		ret = metal_linux_probe_driver(lbus, ldrv);

		/* Log each probe result so test harness can see control flow/state */
		logf = fopen("/tmp/logs/228.txt", "a");
		if (logf) {
			fprintf(logf, "  probed driver (ldrv=%p) -> ret=%d\n", (void *)ldrv, ret);
			fclose(logf);
		}

		/* Clear the error if any driver is available */
		if (!ret)
			error = ret;
	}

	logf = fopen("/tmp/logs/228.txt", "a");
	if (logf) {
		fprintf(logf, "  after probing drivers: error=%d\n", error);
		fclose(logf);
	}

	if (error) {
		/* Log bus close event and return value */
		logf = fopen("/tmp/logs/228.txt", "a");
		if (logf) {
			fprintf(logf, "  no driver matched, closing bus (bus=%p) and returning error=%d\n", (void *)&lbus->bus, error);
			fclose(logf);
		}

		metal_linux_bus_close(&lbus->bus);
		return error;
	}

	/* Attempt to register bus and log the outcome */
	error = metal_linux_register_bus(lbus);
	logf = fopen("/tmp/logs/228.txt", "a");
	if (logf) {
		fprintf(logf, "  metal_linux_register_bus returned %d\n", error);
		fclose(logf);
	}
	if (error)
		metal_linux_bus_close(&lbus->bus);

	/* Final state log before returning */
	logf = fopen("/tmp/logs/228.txt", "a");
	if (logf) {
		fprintf(logf, "metal_linux_probe_bus: exiting (return=%d)\n", error);
		fclose(logf);
	}

	return error;
}
