static int metal_linux_probe_bus(struct linux_bus *lbus)
{
	struct linux_driver *ldrv;
	int ret, error = -ENODEV;
	FILE *log = fopen("/tmp/logs/223.txt", "a");

	if (log) {
		/* Log function entry and basic pointers. Do NOT attempt to print strings (to avoid invoking strlen). */
		fprintf(log, "ENTER: metal_linux_probe_bus: lbus=%p\n", (void *)lbus);
		if (lbus) {
			fprintf(log, "  lbus->bus_name pointer=%p\n", (void *)lbus->bus_name);
		} else {
			fprintf(log, "  lbus is NULL\n");
		}
		fflush(log);
	}

	lbus->sbus = sysfs_open_bus(lbus->bus_name);

	if (log) {
		fprintf(log, "  sysfs_open_bus returned sbus=%p\n", (void *)lbus->sbus);
		fflush(log);
	}

	if (!lbus->sbus) {
		if (log) {
			fprintf(log, "  sbus is NULL -> returning -ENODEV\n");
			fclose(log);
		}
		return -ENODEV;
	}

	for_each_linux_driver(lbus, ldrv) {
		ret = metal_linux_probe_driver(lbus, ldrv);
		if (log) {
			fprintf(log, "  probed driver=%p -> ret=%d\n", (void *)ldrv, ret);
			fflush(log);
		}
		/* Clear the error if any driver is available */
		if (!ret)
			error = ret;
	}

	if (error) {
		if (log) {
			fprintf(log, "  no drivers available (error=%d) -> closing bus and returning error\n", error);
			fflush(log);
		}
		metal_linux_bus_close(&lbus->bus);
		if (log)
			fclose(log);
		return error;
	}

	error = metal_linux_register_bus(lbus);
	if (log) {
		fprintf(log, "  metal_linux_register_bus returned error=%d\n", error);
		fflush(log);
	}
	if (error)
		metal_linux_bus_close(&lbus->bus);

	if (log)
		fclose(log);

	return error;
}
