static int metal_linux_probe_bus(struct linux_bus *lbus)
{
	struct linux_driver *ldrv;
	int ret, error = -ENODEV;

	/* Forward-declare FILE type and stdio functions so we can log to a file
	 * without adding global includes. We only use these for debugging/logging
	 * to /tmp/logs/223.txt and keep the original control flow intact. */
	struct FILE;
	extern struct FILE *fopen(const char *, const char *);
	extern int fprintf(struct FILE *, const char *, ...);
	extern int fflush(struct FILE *);
	extern int fclose(struct FILE *);

	struct FILE *log = NULL;

	/* Open log for append. If opening fails, continue normal execution. */
	log = fopen("/tmp/logs/223.txt", "a");
	if (log) {
		fprintf(log, "ENTER metal_linux_probe_bus: lbus=%p\n", (void *)lbus);
		if (lbus) {
			/* Log pointer values only to avoid dereferencing potentially invalid memory. */
			fprintf(log, "  lbus->bus_name=%p, lbus->sbus=%p, &lbus->bus=%p\n",
				(void *)lbus->bus_name, (void *)lbus->sbus, (void *)&lbus->bus);
		}
		fflush(log);
	}

	lbus->sbus = sysfs_open_bus(lbus->bus_name);
	if (!lbus->sbus) {
		if (log) {
			fprintf(log, "  sysfs_open_bus failed for bus_name=%p -> returning -ENODEV\n",
				(void *)lbus->bus_name);
			fflush(log);
			fclose(log);
		}
		return -ENODEV;
	}

	for_each_linux_driver(lbus, ldrv) {
		if (log)
			fprintf(log, "  for_each_linux_driver: probing ldrv=%p\n", (void *)ldrv);
		fflush(log);

		ret = metal_linux_probe_driver(lbus, ldrv);

		if (log)
			fprintf(log, "    metal_linux_probe_driver returned %d for ldrv=%p\n", ret, (void *)ldrv);
		fflush(log);

		/* Clear the error if any driver is available */
		if (!ret)
			error = ret;
	}

	if (error) {
		if (log) {
			fprintf(log, "  No driver available (error=%d). Closing bus %p and returning error.\n",
				error, (void *)&lbus->bus);
			fflush(log);
			fclose(log);
		}
		metal_linux_bus_close(&lbus->bus);
		return error;
	}

	if (log) {
		fprintf(log, "  About to call metal_linux_register_bus: lbus=%p, bus_name=%p\n",
			(void *)lbus, (void *)lbus->bus_name);
		fflush(log);
	}

	error = metal_linux_register_bus(lbus);

	if (log) {
		fprintf(log, "  metal_linux_register_bus returned %d for lbus=%p\n", error, (void *)lbus);
		fflush(log);
	}

	if (error)
		metal_linux_bus_close(&lbus->bus);

	if (log) {
		if (error)
			fprintf(log, "  Exiting with error=%d (bus closed).\n", error);
		else
			fprintf(log, "  Exiting successfully (error=%d).\n", error);
		fflush(log);
		fclose(log);
	}

	return error;
}
