static int metal_linux_probe_bus(struct linux_bus *lbus)
{
	struct linux_driver *ldrv;
	int ret, error = -ENODEV;
	FILE *logf = NULL;

	/* Open log file for appending; best-effort logging only. */
	logf = fopen("/tmp/logs/225.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER: metal_linux_probe_bus lbus=%p\n", (void *)lbus);
		if (lbus) {
			fprintf(logf, "  bus_name=%s\n", lbus->bus_name ? lbus->bus_name : "(null)");
		}
		fflush(logf);
	}

	lbus->sbus = sysfs_open_bus(lbus->bus_name);
	if (logf) {
		fprintf(logf, "  sysfs_open_bus returned sbus=%p\n", (void *)lbus->sbus);
		fflush(logf);
	}
	if (!lbus->sbus) {
		if (logf) {
			fprintf(logf, "EXIT: sysfs_open_bus failed, returning -ENODEV\n");
			fclose(logf);
		}
		return -ENODEV;
	}

	for_each_linux_driver(lbus, ldrv) {
		/* Log the driver pointer being iterated and the fact we call the probe */
		if (logf) {
			fprintf(logf, "  ITER: ldrv=%p -> calling metal_linux_probe_driver\n", (void *)ldrv);
			fflush(logf);
		}

		ret = metal_linux_probe_driver(lbus, ldrv);

		if (logf) {
			fprintf(logf, "  PROBE_RET: driver=%p ret=%d\n", (void *)ldrv, ret);
			fflush(logf);
		}

		/* Clear the error if any driver is available */
		if (!ret)
			error = ret;
	}

	if (logf) {
		fprintf(logf, "AFTER_LOOP: error=%d\n", error);
		fflush(logf);
	}

	if (error) {
		if (logf) {
			fprintf(logf, "ERROR_PATH: calling metal_linux_bus_close for bus=%p\n", (void *)&lbus->bus);
			fflush(logf);
		}
		metal_linux_bus_close(&lbus->bus);
		if (logf) {
			fprintf(logf, "EXIT: error path closed bus, returning %d\n", error);
			fclose(logf);
		}
		return error;
	}

	error = metal_linux_register_bus(lbus);
	if (logf) {
		fprintf(logf, "metal_linux_register_bus returned %d\n", error);
		fflush(logf);
	}
	if (error)
		metal_linux_bus_close(&lbus->bus);

	if (logf) {
		fprintf(logf, "EXIT: final return %d\n", error);
		fclose(logf);
	}

	return error;
}
