static int metal_linux_probe_driver(struct linux_bus *lbus,
				    struct linux_driver *ldrv)
{
	char command[256];
	int ret;
	FILE *fp = fopen("/tmp/logs/290.txt", "a");
	const char *mod_name = "(null)";
	const char *drv_name = "(null)";
	const char *bus_name = "(null)";

	if (ldrv) {
		if (ldrv->mod_name)
			mod_name = ldrv->mod_name;
		if (ldrv->drv_name)
			drv_name = ldrv->drv_name;
	}
	if (lbus && lbus->bus_name)
		bus_name = lbus->bus_name;

	if (fp) {
		fprintf(fp, "ENTER: %s lbus=%p bus_name=%s ldrv=%p drv_name=%s mod_name=%s sdrv=%p\n",
			__func__, (void *)lbus, bus_name, (void *)ldrv, drv_name, mod_name,
			(ldrv ? (void *)ldrv->sdrv : (void *)0));
		fflush(fp);
	}

	ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

	if (fp) {
		fprintf(fp, "%s: after initial sysfs_open_driver sdrv=%p\n",
			__func__, (void *)ldrv->sdrv);
		fflush(fp);
	}

	/* Try probing the module and then open the driver. */
	if (!ldrv->sdrv) {
		ret = snprintf(command, sizeof(command),
			       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);
		if (fp) {
			fprintf(fp, "%s: snprintf returned %d (limit %zu) command='%s'\n",
				__func__, ret, (size_t)sizeof(command), command);
			fflush(fp);
		}
		if (ret >= (int)sizeof(command)) {
			if (fp) {
				fprintf(fp, "%s: snprintf overflow -> returning -EOVERFLOW\n",
					__func__);
				fclose(fp);
			}
			return -EOVERFLOW;
		}
		ret = system(command);
		if (fp) {
			fprintf(fp, "%s: system('%s') returned %d\n", __func__, command, ret);
			fflush(fp);
		}
		if (ret < 0) {
			/* Original code would call metal_log here; avoid calling it to
			   prevent any indirect recursion during logging. Log the event to file. */
			if (fp) {
				fprintf(fp, "%s: system command failed (ret=%d). metal_log not called here to avoid recursion.\n",
					__func__, ret);
				fflush(fp);
			}
		}
		ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
		if (fp) {
			fprintf(fp, "%s: after second sysfs_open_driver sdrv=%p\n",
				__func__, (void *)ldrv->sdrv);
			fflush(fp);
		}
	}

	/* Try sudo probing the module and then open the driver. */
	if (!ldrv->sdrv) {
		ret = snprintf(command, sizeof(command),
			       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);
		if (fp) {
			fprintf(fp, "%s: snprintf(sudo) returned %d command='%s'\n",
				__func__, ret, command);
			fflush(fp);
		}
		if (ret >= (int)sizeof(command)) {
			if (fp) {
				fprintf(fp, "%s: snprintf overflow (sudo) -> returning -EOVERFLOW\n",
					__func__);
				fclose(fp);
			}
			return -EOVERFLOW;
		}
		ret = system(command);
		if (fp) {
			fprintf(fp, "%s: system(sudo '%s') returned %d\n", __func__, command, ret);
			fflush(fp);
		}
		if (ret < 0) {
			if (fp) {
				fprintf(fp, "%s: sudo system command failed (ret=%d). metal_log not called here to avoid recursion.\n",
					__func__, ret);
				fflush(fp);
			}
		}
		ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
		if (fp) {
			fprintf(fp, "%s: after sudo sysfs_open_driver sdrv=%p\n",
				__func__, (void *)ldrv->sdrv);
			fflush(fp);
		}
	}

	/* If all else fails... */
	if (fp) {
		fprintf(fp, "EXIT: %s returning %d final sdrv=%p\n",
			__func__, (ldrv->sdrv ? 0 : -ENODEV), (void *)ldrv->sdrv);
		fclose(fp);
	}

	return ldrv->sdrv ? 0 : -ENODEV;
}
