static int metal_linux_probe_driver(struct linux_bus *lbus,
                                    struct linux_driver *ldrv)
{
    char command[256];
    int ret;
    FILE *logf = NULL;

    /* Open log file for evidence collection; best-effort, do not fail if it can't be opened */
    logf = fopen("/tmp/logs/295.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER %s: lbus=%p, ldrv=%p\n", __func__, (void *)lbus, (void *)ldrv);
        fprintf(logf, "  lbus->bus_name=%s, ldrv->drv_name=%s, ldrv->mod_name=%s\n",
                (lbus && lbus->bus_name) ? lbus->bus_name : "(null)",
                (ldrv && ldrv->drv_name) ? ldrv->drv_name : "(null)",
                (ldrv && ldrv->mod_name) ? ldrv->mod_name : "(null)");
    }

    ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
    if (logf) {
        fprintf(logf, "  after initial sysfs_open_driver: sdrv=%p\n", (void *)ldrv->sdrv);
        fflush(logf);
    }

    /* Try probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        if (logf) fprintf(logf, "  attempting modprobe (non-sudo) with mod_name=%s\n",
                         (ldrv && ldrv->mod_name) ? ldrv->mod_name : "(null)");

        ret = snprintf(command, sizeof(command),
                       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        if (logf) {
            fprintf(logf, "  snprintf returned %d, sizeof(command)=%zu, command='%s'\n",
                    ret, sizeof(command), (ret >= 0) ? command : "(snprintf failed)");
            fflush(logf);
        }
        if (ret >= (int)sizeof(command)) {
            if (logf) {
                fprintf(logf, "  snprintf overflow detected, returning -EOVERFLOW\n");
                fclose(logf);
            }
            return -EOVERFLOW;
        }
        ret = system(command);
        if (logf) {
            fprintf(logf, "  system(command) returned %d\n", ret);
            fflush(logf);
        }
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            if (logf) {
                fprintf(logf, "  metal_log: system execution failed for command '%s'\n", command);
                fflush(logf);
            }
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        if (logf) {
            fprintf(logf, "  after modprobe sysfs_open_driver: sdrv=%p\n", (void *)ldrv->sdrv);
            fflush(logf);
        }
    }

    /* Try sudo probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        if (logf) fprintf(logf, "  attempting sudo modprobe with mod_name=%s\n",
                         (ldrv && ldrv->mod_name) ? ldrv->mod_name : "(null)");

        ret = snprintf(command, sizeof(command),
                       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        if (logf) {
            fprintf(logf, "  snprintf returned %d, sizeof(command)=%zu, command='%s'\n",
                    ret, sizeof(command), (ret >= 0) ? command : "(snprintf failed)");
            fflush(logf);
        }
        if (ret >= (int)sizeof(command)) {
            if (logf) {
                fprintf(logf, "  snprintf overflow detected (sudo), returning -EOVERFLOW\n");
                fclose(logf);
            }
            return -EOVERFLOW;
        }
        ret = system(command);
        if (logf) {
            fprintf(logf, "  system(command) returned %d (sudo)\n", ret);
            fflush(logf);
        }
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            if (logf) {
                fprintf(logf, "  metal_log: sudo system execution failed for command '%s'\n", command);
                fflush(logf);
            }
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        if (logf) {
            fprintf(logf, "  after sudo modprobe sysfs_open_driver: sdrv=%p\n", (void *)ldrv->sdrv);
            fflush(logf);
        }
    }

    /* If all else fails... */
    if (logf) {
        int final_ret = ldrv->sdrv ? 0 : -ENODEV;
        fprintf(logf, "EXIT %s: sdrv=%p, returning %d\n", __func__, (void *)ldrv->sdrv, final_ret);
        fclose(logf);
    }

    return ldrv->sdrv ? 0 : -ENODEV;
}
