static int metal_linux_probe_driver(struct linux_bus *lbus,
				    struct linux_driver *ldrv)
{
	char command[256];
	int ret;
	FILE *log = fopen("/tmp/logs/295.txt", "a");

	/* Basic entry log to ensure the function invocation is recorded. */
	if (log) {
		fprintf(log, "ENTER: %s() called\n", __func__);
		fprintf(log, "  lbus ptr=%p, ldrv ptr=%p\n", (void *)lbus, (void *)ldrv);
		fprintf(log, "  lbus->bus_name ptr=%p, value=%s\n",
			lbus ? (void *)lbus->bus_name : (void *)0,
			lbus && lbus->bus_name ? lbus->bus_name : "(null)");
		fprintf(log, "  ldrv->drv_name ptr=%p, value=%s\n",
			ldrv ? (void *)ldrv->drv_name : (void *)0,
			ldrv && ldrv->drv_name ? ldrv->drv_name : "(null)");
		fprintf(log, "  ldrv->mod_name ptr=%p, value=%s\n",
			ldrv ? (void *)ldrv->mod_name : (void *)0,
			ldrv && ldrv->mod_name ? ldrv->mod_name : "(null)");
		fflush(log);
	}

	/* First attempt: try to open the driver via sysfs. */
	if (log) fprintf(log, "CALL: sysfs_open_driver(bus_name=%s, drv_name=%s)\n",
		lbus && lbus->bus_name ? lbus->bus_name : "(null)",
		ldrv && ldrv->drv_name ? ldrv->drv_name : "(null)");
	if (log) fflush(log);

	ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

	if (log) {
		fprintf(log, "RETURN: sysfs_open_driver -> sdrv ptr=%p\n", (void *)ldrv->sdrv);
		fflush(log);
	}

	/* Try probing the module and then open the driver. */
	if (!ldrv->sdrv) {
		ret = snprintf(command, sizeof(command),
			       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);
		if (log) fprintf(log, "INFO: snprintf produced ret=%d, command='%s'\n", ret, command);
		if (log) fflush(log);
		if (ret >= (int)sizeof(command)) {
			if (log) {
				fprintf(log, "ERROR: command buffer overflow detected (ret=%d, buf=%zu)\n",
					ret, (size_t)sizeof(command));
				fflush(log);
			}
			return -EOVERFLOW;
		}
		ret = system(command);
		if (ret < 0) {
			metal_log(METAL_LOG_WARNING,
				  "%s: executing system command '%s' failed.\n",
				  __func__, command);
			if (log) {
				fprintf(log, "WARN: system('%s') failed with ret=%d\n", command, ret);
				fflush(log);
			}
		} else {
			if (log) {
				fprintf(log, "INFO: system('%s') returned %d\n", command, ret);
				fflush(log);
			}
		}
		if (log) fprintf(log, "CALL: sysfs_open_driver(bus_name=%s, drv_name=%s) [after modprobe]\n",
			lbus && lbus->bus_name ? lbus->bus_name : "(null)",
			ldrv && ldrv->drv_name ? ldrv->drv_name : "(null)");
		if (log) fflush(log);
		ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
		if (log) {
			fprintf(log, "RETURN: sysfs_open_driver -> sdrv ptr=%p\n", (void *)ldrv->sdrv);
			fflush(log);
		}
	}

	/* Try sudo probing the module and then open the driver. */
	if (!ldrv->sdrv) {
		ret = snprintf(command, sizeof(command),
			       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);
		if (log) fprintf(log, "INFO: snprintf produced ret=%d, command='%s'\n", ret, command);
		if (log) fflush(log);
		if (ret >= (int)sizeof(command)) {
			if (log) {
				fprintf(log, "ERROR: command buffer overflow detected (ret=%d, buf=%zu)\n",
					ret, (size_t)sizeof(command));
				fflush(log);
			}
			return -EOVERFLOW;
		}
		ret = system(command);
		if (ret < 0) {
			metal_log(METAL_LOG_WARNING,
				  "%s: executing system command '%s' failed.\n",
				  __func__, command);
			if (log) {
				fprintf(log, "WARN: system('%s') failed with ret=%d\n", command, ret);
				fflush(log);
			}
		} else {
			if (log) {
				fprintf(log, "INFO: system('%s') returned %d\n", command, ret);
				fflush(log);
			}
		}
		if (log) fprintf(log, "CALL: sysfs_open_driver(bus_name=%s, drv_name=%s) [after sudo modprobe]\n",
			lbus && lbus->bus_name ? lbus->bus_name : "(null)",
			ldrv && ldrv->drv_name ? ldrv->drv_name : "(null)");
		if (log) fflush(log);
		ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
		if (log) {
			fprintf(log, "RETURN: sysfs_open_driver -> sdrv ptr=%p\n", (void *)ldrv->sdrv);
			fflush(log);
		}
	}

	/* Final result logging */
	if (log) {
		if (ldrv->sdrv) {
			fprintf(log, "EXIT: %s -> success (sdrv=%p)\n", __func__, (void *)ldrv->sdrv);
		} else {
			fprintf(log, "EXIT: %s -> failure (no sdrv found)\n", __func__);
		}
		fflush(log);
		fclose(log);
	}

	/* If all else fails... */
	return ldrv->sdrv ? 0 : -ENODEV;
}
