static int metal_linux_probe_driver(struct linux_bus *lbus,
                                    struct linux_driver *ldrv)
{
    char command[256];
    int ret;
    FILE *log = fopen("/tmp/logs/295.txt", "a");

    if (log) {
        fprintf(log, "ENTER %s: lbus=%p, ldrv=%p\n", __func__, (void *)lbus, (void *)ldrv);
        /* Attempt to log string members; these may be NULL in some contexts. */
        if (lbus) {
            fprintf(log, "  lbus->bus_name=%s\n", lbus->bus_name ? lbus->bus_name : "(null)");
        } else {
            fprintf(log, "  lbus is NULL\n");
        }
        if (ldrv) {
            fprintf(log, "  ldrv->drv_name=%s, ldrv->mod_name=%s, ldrv->sdrv=%p\n",
                    ldrv->drv_name ? ldrv->drv_name : "(null)",
                    ldrv->mod_name ? ldrv->mod_name : "(null)",
                    (void *)ldrv->sdrv);
        } else {
            fprintf(log, "  ldrv is NULL\n");
        }
        fflush(log);
    }

    ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);

    if (log) {
        fprintf(log, "AFTER initial sysfs_open_driver: ldrv->sdrv=%p\n", (void *)ldrv->sdrv);
        fflush(log);
    }

    /* Try probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        if (log) {
            fprintf(log, "  snprintf returned %d, command='%s'\n", ret, command);
            fflush(log);
        }
        if (ret >= (int)sizeof(command)) {
            if (log) {
                fprintf(log, "  snprintf overflow detected, returning -EOVERFLOW\n");
                fclose(log);
            }
            return -EOVERFLOW;
        }
        ret = system(command);
        if (log) {
            fprintf(log, "  system(command) returned %d\n", ret);
            fflush(log);
        }
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            if (log) {
                fprintf(log, "  system call failed (ret=%d), metal_log invoked\n", ret);
                fflush(log);
            }
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        if (log) {
            fprintf(log, "  AFTER sysfs_open_driver (post-modprobe): ldrv->sdrv=%p\n", (void *)ldrv->sdrv);
            fflush(log);
        }
    }

    /* Try sudo probing the module and then open the driver. */
    if (!ldrv->sdrv) {
        ret = snprintf(command, sizeof(command),
                       "sudo modprobe %s > /dev/null 2>&1", ldrv->mod_name);
        if (log) {
            fprintf(log, "  snprintf (sudo) returned %d, command='%s'\n", ret, command);
            fflush(log);
        }
        if (ret >= (int)sizeof(command)) {
            if (log) {
                fprintf(log, "  snprintf overflow detected (sudo), returning -EOVERFLOW\n");
                fclose(log);
            }
            return -EOVERFLOW;
        }
        ret = system(command);
        if (log) {
            fprintf(log, "  system(sudo command) returned %d\n", ret);
            fflush(log);
        }
        if (ret < 0) {
            metal_log(METAL_LOG_WARNING,
                      "%s: executing system command '%s' failed.\n",
                      __func__, command);
            if (log) {
                fprintf(log, "  sudo system call failed (ret=%d), metal_log invoked\n", ret);
                fflush(log);
            }
        }
        ldrv->sdrv = sysfs_open_driver(lbus->bus_name, ldrv->drv_name);
        if (log) {
            fprintf(log, "  AFTER sysfs_open_driver (post-sudo-modprobe): ldrv->sdrv=%p\n", (void *)ldrv->sdrv);
            fflush(log);
        }
    }

    /* If all else fails... */
    if (log) {
        int final_ret = ldrv->sdrv ? 0 : -ENODEV;
        fprintf(log, "EXIT %s: final ldrv->sdrv=%p, returning %d\n", __func__, (void *)ldrv->sdrv, final_ret);
        fclose(log);
    }

    return ldrv->sdrv ? 0 : -ENODEV;
}
