int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
	      void **result)
{
	int prot = PROT_READ | PROT_WRITE, error;
	void *mem;
	FILE *log = fopen("/tmp/logs/702.txt", "a");
	if (log) {
		fprintf(log, "ENTER metal_map: fd=%d, offset=%lld, size=%llu, expand=%d, flags=0x%x, result=%p\n",
			fd, (long long)offset, (unsigned long long)size, expand, flags, (void *)result);
		fflush(log);
	}

	/* Original code modifies the parameter 'flags' here. Log before change. */
	if (log) {
		fprintf(log, "Before flags modification: flags=0x%x\n", flags);
		fflush(log);
	}

	flags |= MAP_SHARED;

	if (log) {
		fprintf(log, "After flags |= MAP_SHARED: flags=0x%x\n", flags);
		fflush(log);
	}

	if (fd < 0) {
		/* This branch assigns to the parameter 'fd' and 'flags' (demonstrating modification of parameters). */
		if (log) {
			fprintf(log, "Branch: fd < 0 (fd=%d). Setting fd = -1 and flags = MAP_PRIVATE | MAP_ANONYMOUS\n", fd);
			fflush(log);
		}
		fd = -1;
		flags = MAP_PRIVATE | MAP_ANONYMOUS;
		if (log) {
			fprintf(log, "After fd<0 assignments: fd=%d, flags=0x%x\n", fd, flags);
			fflush(log);
		}
	} else if (expand) {
		off_t reqsize = offset + size;
		struct stat stat;

		if (log) {
			fprintf(log, "Branch: expand (expand=%d). reqsize=%lld\n", expand, (long long)reqsize);
			fflush(log);
		}

		error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
		if (log) {
			fprintf(log, "After flock(LOCK_EX): error=%d, errno=%d\n", error, errno);
			fflush(log);
		}
		if (!error)
			error = fstat(fd, &stat);
		if (log) {
			fprintf(log, "After fstat: error=%d, stat.st_size=%lld\n", error, (long long)stat.st_size);
			fflush(log);
		}
		if (!error && stat.st_size < reqsize)
			error = ftruncate(fd, reqsize);
		if (log) {
			fprintf(log, "After possible ftruncate: error=%d\n", error);
			fflush(log);
		}
		if (!error)
			flock(fd, LOCK_UN);
		if (error) {
			if (log) {
				fprintf(log, "Error during expand handling: error=%d, errno=%d. Returning -errno=%d\n", error, errno, -errno);
				fflush(log);
			}
			if (log) fclose(log);
			return -errno;
		}
	}

	if (log) {
		fprintf(log, "About to call mmap: size=%llu, prot=0x%x, flags=0x%x, fd=%d, offset=%lld\n",
			(unsigned long long)size, prot, flags, fd, (long long)offset);
		fflush(log);
	}

	mem = mmap(NULL, size, prot, flags, fd, offset);
	if (mem == MAP_FAILED) {
		if (log) {
			fprintf(log, "mmap failed: mem=MAP_FAILED, errno=%d. Returning -errno=%d\n", errno, -errno);
			fflush(log);
			fclose(log);
		}
		return -errno;
	}
	*result = mem;

	if (log) {
		fprintf(log, "mmap succeeded: mem=%p, *result=%p. Returning 0\n", mem, mem);
		fflush(log);
		fclose(log);
	}
	return 0;
}
