int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
#include <stdio.h>
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;
    FILE *logf = fopen("/tmp/logs/702.txt", "a");

    if (logf) {
        /* Log entry and initial parameter state */
        fprintf(logf, "metal_map(): entered - fd=%d, offset=%lld, size=%zu, expand=%d, flags=0x%x, result=%p\n",
                fd, (long long)offset, size, expand, flags, (void *)result);
        fflush(logf);
    }

    /* Original behavior: add MAP_SHARED */
    flags |= MAP_SHARED;
    if (logf)
        fprintf(logf, "metal_map(): after flags |= MAP_SHARED -> flags=0x%x\n", flags);

    if (fd < 0) {
        if (logf)
            fprintf(logf, "metal_map(): fd < 0 branch taken (fd=%d). Current flags before assignment=0x%x\n",
                    fd, flags);

        /* This assignment modifies the function parameter 'flags' (purposefully preserved
         * to reproduce the reported Rule 17.8 scenario). Log the before/after values. */
        flags = MAP_PRIVATE | MAP_ANONYMOUS;

        if (logf)
            fprintf(logf, "metal_map(): assigned flags = MAP_PRIVATE | MAP_ANONYMOUS -> flags=0x%x\n",
                    flags);

        fd = -1;
        if (logf)
            fprintf(logf, "metal_map(): fd set to -1 to indicate anonymous mapping.\n");
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;

        if (logf)
            fprintf(logf, "metal_map(): expand branch taken. reqsize=%lld\n", (long long)reqsize);

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        if (logf)
            fprintf(logf, "metal_map(): after flock(LOCK_EX) -> error=%d, errno=%d\n", error, errno);

        if (!error)
            error = fstat(fd, &stat);
        if (logf && !error)
            fprintf(logf, "metal_map(): fstat succeeded. file size=%lld\n", (long long)stat.st_size);

        if (!error && stat.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        if (logf)
            fprintf(logf, "metal_map(): after possible ftruncate -> error=%d\n", error);

        if (!error)
            flock(fd, LOCK_UN);
        if (error) {
            if (logf) {
                fprintf(logf, "metal_map(): expand branch error detected -> returning -errno (errno=%d)\n", errno);
                fclose(logf);
            }
            return -errno;
        }
    }

    if (logf)
        fprintf(logf, "metal_map(): calling mmap(NULL, %zu, prot=0x%x, flags=0x%x, fd=%d, offset=%lld)\n",
                size, prot, flags, fd, (long long)offset);

    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        if (logf) {
            fprintf(logf, "metal_map(): mmap failed -> returning -errno (errno=%d)\n", errno);
            fclose(logf);
        }
        return -errno;
    }

    *result = mem;

    if (logf) {
        fprintf(logf, "metal_map(): mmap succeeded -> mem=%p, result set, returning 0\n", mem);
        fclose(logf);
    }

    return 0;
}
