int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Logging: write control flow and state to /tmp/logs/708.txt. */
    {
        int _saved_errno = errno;
        int _logfd = open("/tmp/logs/708.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (_logfd >= 0) {
            char _buf[256];
            int _len = snprintf(_buf, sizeof(_buf),
                                "metal_map ENTRY: fd=%d offset=%lld size=%zu expand=%d flags=0x%x prot=0x%x\n",
                                fd, (long long)offset, size, expand, flags, prot);
            if (_len > 0)
                (void)write(_logfd, _buf, (size_t)_len);
            close(_logfd);
        }
        errno = _saved_errno;
    }

    flags |= MAP_SHARED;

    if (fd < 0) {
        /* Log state before modifying fd/flags. */
        {
            int _saved_errno = errno;
            int _logfd = open("/tmp/logs/708.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
            if (_logfd >= 0) {
                char _buf[256];
                int _len = snprintf(_buf, sizeof(_buf),
                                    "branch: fd<0 taken; before: fd=%d flags=0x%x\n",
                                    fd, flags);
                if (_len > 0)
                    (void)write(_logfd, _buf, (size_t)_len);
                close(_logfd);
            }
            errno = _saved_errno;
        }

        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;

        /* Log state after modifying fd/flags to capture the potentially dead assignment. */
        {
            int _saved_errno = errno;
            int _logfd = open("/tmp/logs/708.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
            if (_logfd >= 0) {
                char _buf[256];
                int _len = snprintf(_buf, sizeof(_buf),
                                    "branch: fd<0 taken; after: fd=%d flags=0x%x (MAP_ANONYMOUS=%d)\n",
                                    fd, flags, (flags & MAP_ANONYMOUS) ? 1 : 0);
                if (_len > 0)
                    (void)write(_logfd, _buf, (size_t)_len);
                close(_logfd);
            }
            errno = _saved_errno;
        }
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        if (!error)
            error = fstat(fd, &stat);
        if (!error && stat.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        if (!error)
            flock(fd, LOCK_UN);
        if (error)
            return -errno;
    }

    /* Log parameters that will be passed to mmap. */
    {
        int _saved_errno = errno;
        int _logfd = open("/tmp/logs/708.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (_logfd >= 0) {
            char _buf[256];
            int _len = snprintf(_buf, sizeof(_buf),
                                "calling mmap: fd=%d offset=%lld size=%zu flags=0x%x prot=0x%x\n",
                                fd, (long long)offset, size, flags, prot);
            if (_len > 0)
                (void)write(_logfd, _buf, (size_t)_len);
            close(_logfd);
        }
        errno = _saved_errno;
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);

    /* Log the result of mmap. */
    {
        int _saved_errno = errno;
        int _logfd = open("/tmp/logs/708.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (_logfd >= 0) {
            char _buf[256];
            int _len;
            if (mem == MAP_FAILED) {
                _len = snprintf(_buf, sizeof(_buf),
                                "mmap RESULT: MAP_FAILED errno=%d\n",
                                errno);
            } else {
                _len = snprintf(_buf, sizeof(_buf),
                                "mmap RESULT: mem=%p errno=%d\n",
                                mem, errno);
            }
            if (_len > 0)
                (void)write(_logfd, _buf, (size_t)_len);
            close(_logfd);
        }
        errno = _saved_errno;
    }

    if (mem == MAP_FAILED)
        return -errno;
    *result = mem;
    return 0;
}
