int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;
    FILE *log = fopen("/tmp/logs/705.txt", "a");

    if (log) {
        fprintf(log, "ENTER metal_map: fd=%d, offset=%lld, size=%zu, expand=%d, flags=0x%x, result=%p\n",
                fd, (long long)offset, size, expand, flags, (void *)result);
        fflush(log);
    }

    flags |= MAP_SHARED;

    if (fd < 0) {
        if (log) fprintf(log, "branch: fd < 0, switching to anonymous private mapping\n");
        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;
        int ret;
        int saved_errno = 0;

        /* Acquire exclusive lock */
        ret = flock(fd, LOCK_EX);
        saved_errno = errno;
        error = (ret < 0) ? -saved_errno : 0;
        if (log) fprintf(log, "flock(LOCK_EX): ret=%d, errno=%d, error=%d\n", ret, saved_errno, error);

        if (!error) {
            ret = fstat(fd, &stat);
            saved_errno = errno;
            /* preserve original assignment behavior */
            error = ret;
            if (log) fprintf(log, "fstat: ret=%d, errno=%d, st_size=%lld\n", ret, saved_errno, (long long)stat.st_size);
        }

        if (!error && stat.st_size < reqsize) {
            ret = ftruncate(fd, reqsize);
            saved_errno = errno;
            error = ret;
            if (log) fprintf(log, "ftruncate: reqsize=%lld, ret=%d, errno=%d\n", (long long)reqsize, ret, saved_errno);
        }

        if (!error) {
            ret = flock(fd, LOCK_UN);
            saved_errno = errno;
            if (log) fprintf(log, "flock(LOCK_UN): ret=%d, errno=%d\n", ret, saved_errno);
        }

        if (error) {
            /* original code returned -errno here; preserve that behavior by returning -saved_errno */
            if (log) {
                fprintf(log, "expand branch failed: error=%d, returning -errno (%d)\n", error, -saved_errno);
                fclose(log);
            }
            return -saved_errno;
        }
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        int saved_errno = errno;
        if (log) {
            fprintf(log, "mmap failed: size=%zu, prot=0x%x, flags=0x%x, fd=%d, offset=%lld, errno=%d\n",
                    size, prot, flags, fd, (long long)offset, saved_errno);
            fclose(log);
        }
        return -saved_errno;
    }

    *result = mem;
    if (log) {
        fprintf(log, "mmap success: mem=%p, returning 0\n", mem);
        fclose(log);
    }
    return 0;
}
