int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Minimal entry log so the function reach is always recorded. */
    {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "ENTER metal_map: fd=%d, offset=%lld, size=%zu, expand=%d, flags=0x%x, result=%p\n",
                    fd, (long long)offset, size, expand, flags, (void *)result);
            fclose(logf);
        }
    }

    flags |= MAP_SHARED;

    if (fd < 0) {
        /* Log the branch where an anonymous mapping will be used */
        {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "BRANCH: fd < 0 -> using anonymous mapping (fd set to -1).\n");
                fclose(logf);
            }
        }

        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;

        /* Acquire exclusive lock and attempt to expand file if needed. */
        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        if (error) {
            /* Capture errno now (before any logging) to preserve return semantics. */
            int save_errno = errno;
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "ERROR: flock(LOCK_EX) failed: errno=%d, error=%d\n", save_errno, error);
                fclose(logf);
            }
            return -save_errno;
        }

        if (!error)
            error = fstat(fd, &stat);

        if (!error && stat.st_size < reqsize)
            error = ftruncate(fd, reqsize);

        if (!error)
            flock(fd, LOCK_UN);

        if (error) {
            /* fstat/ftruncate set errno; capture it before logging. */
            int save_errno = errno;
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "ERROR during expand: fstat/ftruncate returned error=%d, errno=%d\n",
                        error, save_errno);
                fclose(logf);
            }
            return -save_errno;
        }

        /* Log that expansion path completed */
        {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "INFO: expand path completed: reqsize=%lld, st_size=%lld\n",
                        (long long)reqsize, (long long)stat.st_size);
                fclose(logf);
            }
        }
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        int save_errno = errno; /* preserve errno for return */
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "ERROR: mmap failed: size=%zu, fd=%d, offset=%lld, errno=%d\n",
                    size, fd, (long long)offset, save_errno);
            fclose(logf);
        }
        return -save_errno;
    }

    *result = mem;

    /* Final success log */
    {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "EXIT metal_map: success, result=%p\n", mem);
            fclose(logf);
        }
    }

    return 0;
}
