int metal_map(int fd, off_t offset, size_t size, int expand, int flags,
              void **result)
{
    int prot = PROT_READ | PROT_WRITE, error;
    void *mem;

    /* Logging: open log file and record entry and parameters */
    {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "ENTER metal_map: fd=%d, offset=%jd, size=%zu, expand=%d, flags=0x%x, result_ptr=%p\n",
                    fd, (intmax_t)offset, size, expand, flags, (void *)result);
            fclose(logf);
        }
    }

    flags |= MAP_SHARED;

    if (fd < 0) {
        /* Log private/anonymous mapping path */
        {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "Path: fd<0 -> using MAP_PRIVATE|MAP_ANONYMOUS, fd set to -1\n");
                fclose(logf);
            }
        }

        fd = -1;
        flags = MAP_PRIVATE | MAP_ANONYMOUS;
    } else if (expand) {
        off_t reqsize = offset + size;
        struct stat stat;

        /* Log entering expand branch and requested size */
        {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "Path: expand branch: reqsize=%jd\n", (intmax_t)reqsize);
                fclose(logf);
            }
        }

        error = flock(fd, LOCK_EX) < 0 ? -errno : 0;
        {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "flock(LOCK_EX) returned %d, errno=%d, error=%d\n", (int)(error ? -error : 0), errno, error);
                fclose(logf);
            }
        }
        if (!error)
            error = fstat(fd, &stat);
        if (!error && stat.st_size < reqsize)
            error = ftruncate(fd, reqsize);
        if (!error)
            flock(fd, LOCK_UN);

        /* Log results of fstat/ftruncate/unlock */
        if (error) {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "expand: error (%d), returning -errno (%d)\n", error, errno);
                fclose(logf);
            }
            return -errno;
        } else {
            FILE *logf = fopen("/tmp/logs/705.txt", "a");
            if (logf) {
                fprintf(logf, "expand: completed successfully, file size after op=%jd\n", (intmax_t)stat.st_size);
                fclose(logf);
            }
        }
    }

    mem = mmap(NULL, size, prot, flags, fd, offset);
    if (mem == MAP_FAILED) {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "mmap failed: mem=MAP_FAILED, errno=%d, returning -errno (%d)\n", errno, -errno);
            fclose(logf);
        }
        return -errno;
    }

    /* Log the mapped address and the assignment about to happen */
    {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "mmap succeeded: mem=%p; assigning *result=mem (result ptr=%p)\n", mem, (void *)result);
            fclose(logf);
        }
    }

    *result = mem;

    /* Final log and return */
    {
        FILE *logf = fopen("/tmp/logs/705.txt", "a");
        if (logf) {
            fprintf(logf, "EXIT metal_map: success, returned 0, result=*result=%p\n", mem);
            fclose(logf);
        }
    }

    return 0;
}
