int metal_open(const char *path, int shm)
{
    const int flags = O_RDWR | O_CREAT | O_CLOEXEC;
    const int mode = S_IRUSR | S_IWUSR;
    int fd;

    /* Probe: log entry into metal_open */
    {
        int logfd = open("/tmp/logs/478.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (logfd >= 0) {
            char buf[256];
            int n = snprintf(buf, sizeof(buf), "metal_open ENTER: path=\"%s\", shm=%d\n",
                             path ? path : "(null)", shm);
            if (n > 0) {
                (void)write(logfd, buf, (size_t)n);
            }
            close(logfd);
        }
    }

    if (!path || !strlen(path)) {
        /* Log invalid path and return same error as original */
        int logfd = open("/tmp/logs/478.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (logfd >= 0) {
            char buf[128];
            int n = snprintf(buf, sizeof(buf), "metal_open: invalid path -> return -EINVAL\n");
            if (n > 0) (void)write(logfd, buf, (size_t)n);
            close(logfd);
        }
        return -EINVAL;
    }

    fd = shm ? shm_open(path, flags, mode) : open(path, flags, mode);

    /* Preserve errno immediately after the open/shm_open call, then log result. */
    if (fd < 0) {
        int saved_errno = errno;
        int logfd = open("/tmp/logs/478.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (logfd >= 0) {
            char buf[512];
            int n = snprintf(buf, sizeof(buf),
                             "metal_open: open failed: path=\"%s\", shm=%d, fd=%d, errno=%d (%s) -> returning %d\n",
                             path, shm, fd, saved_errno, strerror(saved_errno), -saved_errno);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
            close(logfd);
        }
        return -saved_errno;
    }

    /* Log success path */
    {
        int logfd = open("/tmp/logs/478.txt", O_WRONLY | O_CREAT | O_APPEND, 0644);
        if (logfd >= 0) {
            char buf[256];
            int n = snprintf(buf, sizeof(buf), "metal_open: success: path=\"%s\", shm=%d, fd=%d\n",
                             path, shm, fd);
            if (n > 0) (void)write(logfd, buf, (size_t)n);
            close(logfd);
        }
    }

    return fd;
}
