int metal_open(const char *path, int shm)
{
	const int flags = O_RDWR | O_CREAT | O_CLOEXEC;
	const int mode = S_IRUSR | S_IWUSR;
	int fd;
	FILE *log = fopen("/tmp/logs/473.txt", "a");

	if (log) {
		fprintf(log, "metal_open entered: path=%p shm=%d\n", (void *)path, shm);
		fflush(log);
	}

	/* Preserve original behavior: NULL or empty string yields -EINVAL. */
	if (!path) {
		if (log) {
			fprintf(log, "path is NULL -> returning -EINVAL\n");
			fclose(log);
		}
		return -EINVAL;
	}

	/* Log that we are about to call strlen. This is the critical point for the
	   reported violation: if `path` points to a non-terminated buffer, strlen
	   may read beyond the object bounds. We intentionally call strlen here to
	   reproduce that behavior and capture evidence in the log. */
	if (log) {
		fprintf(log, "path non-NULL (%p): about to call strlen(path) -- potential out-of-bounds read if not NUL-terminated\n", (void *)path);
		fflush(log);
	}

	size_t len = strlen(path);

	if (log) {
		fprintf(log, "strlen(path) returned %zu\n", len);
		fflush(log);
	}

	if (len == 0) {
		if (log) {
			fprintf(log, "path is empty string -> returning -EINVAL\n");
			fclose(log);
		}
		return -EINVAL;
	}

	fd = shm ? shm_open(path, flags, mode) : open(path, flags, mode);

	if (log) {
		if (fd < 0)
			fprintf(log, "open/shm_open failed: fd=%d errno=%d -> returning %d\n", fd, errno, -errno);
		else
			fprintf(log, "open/shm_open succeeded: fd=%d -> returning %d\n", fd, fd);
		fclose(log);
	}

	return fd < 0 ? -errno : fd;
}
