int metal_run_noblock(int threads, metal_thread_t child,
		     void *arg, void *tids, int *threads_out)
{
	int error, i;
	pthread_t *tid_p = (pthread_t *)tids;
	/* Logging probe file to capture control flow/state for analysis */
	FILE *log = fopen("/tmp/logs/527.txt", "a");
	if (log) {
		fprintf(log, "ENTER metal_run_noblock: threads=%d, child=%p, arg=%p, tids=%p, threads_out=%p\n",
			threads, (void *)child, arg, tids, (void *)threads_out);
	}

	if (!tids) {
		/* preserve original behavior */
		metal_log(METAL_LOG_ERROR, "invalid argument, tids is NULL.\n");
		if (log) {
			fprintf(log, "ERROR: tids is NULL -> returning -EINVAL\n");
			fclose(log);
		}
		return -EINVAL;
	}

	error = 0;
	for (i = 0; i < threads; i++) {
		if (log) fprintf(log, "Attempting to create thread %d...\n", i);
		/* preserve original semantics: error = -pthread_create(...) */
		int create_ret = pthread_create(&tid_p[i], NULL, child, arg);
		error = -create_ret;
		if (log) {
			if (create_ret == 0) {
				fprintf(log, "Thread %d created successfully (tid=%p). create_ret=%d, error=%d\n",
					i, (void *)&tid_p[i], create_ret, error);
			} else {
				fprintf(log, "Thread %d creation FAILED. create_ret=%d, error=%d, strerror(create_ret)=%s\n",
					i, create_ret, error, strerror(create_ret));
			}
		}
		if (error) {
			metal_log(METAL_LOG_ERROR, "failed to create thread - %s\n",
				  strerror(error));
			break;
		}
	}

	*threads_out = i;
	if (log) {
		fprintf(log, "EXIT metal_run_noblock: created_threads=%d, return_error=%d\n", i, error);
		fclose(log);
	}
	return error;
}
