int metal_shmem_open(const char *name, size_t size,
		     struct metal_io_region **result)
{
	struct metal_page_size *ps;
	int fd, error;
	FILE *logf = NULL;
	size_t initial_size = size;

	/* Open log file for appending; ignore logging failures */
	logf = fopen("/tmp/logs/192.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER metal_shmem_open: name=%s size=%zu result=%p\n",
			name ? name : "(null)", (size_t)size, (void *)result);
	}

	error = metal_shmem_open_generic(name, size, result);
	if (logf) {
		fprintf(logf, "after metal_shmem_open_generic: error=%d size=%zu\n", error, (size_t)size);
	}
	if (!error) {
		if (logf) {
			fprintf(logf, "early return (success).\n");
			if (size != initial_size) {
				fprintf(logf, "VIOLATION DETECTED: parameter 'size' modified: initial=%zu now=%zu\n",
					initial_size, (size_t)size);
			}
			fclose(logf);
		}
		return error;
	}

	error = metal_open(name, 1);
	if (logf) {
		fprintf(logf, "after metal_open: error=%d\n", error);
	}
	if (error < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);
		if (logf) {
			fprintf(logf, "metal_open failed, returning %d\n", error);
			if (size != initial_size) {
				fprintf(logf, "VIOLATION DETECTED: parameter 'size' modified: initial=%zu now=%zu\n",
					initial_size, (size_t)size);
			}
			fclose(logf);
		}
		return error;
	}
	fd = error;

	if (logf) {
		fprintf(logf, "fd=%d initial_size=%zu\n", fd, initial_size);
	}

	/* Iterate through page sizes in decreasing order. */
	metal_for_each_page_size_down(ps) {
		if (logf) {
			fprintf(logf, "loop: ps->page_size=%zu current size=%zu\n", ps->page_size, (size_t)size);
		}
		if (ps->page_size > 2 * size) {
			if (logf) fprintf(logf, "skipping page size %zu (> 2 * size=%zu)\n", ps->page_size, (size_t)size);
			continue;
		}
		error = metal_shmem_try_map(ps, fd, size, result);
		if (logf) {
			fprintf(logf, "after metal_shmem_try_map: error=%d size=%zu\n", error, (size_t)size);
		}
		if (!error) {
			if (logf) {
				if (size != initial_size) {
					fprintf(logf, "VIOLATION DETECTED: parameter 'size' modified inside loop: initial=%zu now=%zu\n",
						initial_size, (size_t)size);
				}
				fprintf(logf, "mapping successful, breaking out of loop.\n");
			}
			break;
		}
	}

	close(fd);
	if (logf) {
		fprintf(logf, "EXIT metal_shmem_open: error=%d final_size=%zu initial_size=%zu\n", error, (size_t)size, initial_size);
		fclose(logf);
	}
	return error;
}
