int metal_shmem_open(const char *name, size_t size,
		     struct metal_io_region **result)
{
	struct metal_page_size *ps;
	int fd, error;
	FILE *log = NULL;

	/* Try to open the required log file. If it fails, we will skip logging. */
	log = fopen("/tmp/logs/195.txt", "a");
	if (log) {
		fprintf(log, "metal_shmem_open ENTER: name=%s, size=%zu, result_ptr=%p\n", 
			name ? name : "(null)", size, (void *)result);
		fflush(log);
	}

	error = metal_shmem_open_generic(name, size, result);
	if (log) {
		fprintf(log, "metal_shmem_open: metal_shmem_open_generic returned %d\n", error);
		fflush(log);
	}
	if (!error) {
		/* Success path from generic open; log and return. */
		if (log) {
			fprintf(log, "metal_shmem_open: generic succeeded, returning %d\n", error);
			fclose(log);
		}
		return error;
	}

	error = metal_open(name, 1);
	if (log) {
		fprintf(log, "metal_shmem_open: metal_open returned %d\n", error);
		fflush(log);
	}
	if (error < 0) {
		metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);
		if (log) {
			fprintf(log, "metal_shmem_open: metal_open failed (error=%d), logging and returning\n", error);
			fclose(log);
		}
		return error;
	}
	fd = error;
	if (log) {
		fprintf(log, "metal_shmem_open: obtained fd=%d\n", fd);
		fflush(log);
	}

	/* Iterate through page sizes in decreasing order. */
	metal_for_each_page_size_down(ps) {
		if (log) {
			fprintf(log, "metal_shmem_open: checking page size candidate ps=%p, page_size=%zu\n",
				(void *)ps, (size_t)ps->page_size);
			fflush(log);
		}
		if (ps->page_size > 2 * size) {
			if (log) {
				fprintf(log, "metal_shmem_open: skipping ps->page_size (%zu) > 2*size (%zu)\n",
					(size_t)ps->page_size, (size_t)(2 * size));
				fflush(log);
			}
			continue;
		}
		error = metal_shmem_try_map(ps, fd, size, result);
		if (log) {
			fprintf(log, "metal_shmem_open: metal_shmem_try_map(ps=%p, fd=%d, size=%zu, result=%p) returned %d\n",
				(void *)ps, fd, size, (void *)result, error);
			fflush(log);
		}
		if (!error) {
			if (log) {
				fprintf(log, "metal_shmem_open: mapping succeeded with ps=%p\n", (void *)ps);
				fflush(log);
			}
			break;
		}
	}

	/* Close the file descriptor opened earlier. */
	if (log) {
		fprintf(log, "metal_shmem_open: closing fd=%d\n", fd);
		fflush(log);
	}
	close(fd);

	if (log) {
		fprintf(log, "metal_shmem_open EXIT: final error=%d, result_ptr=%p\n", error, (void *)result);
		fclose(log);
	}

	return error;
}
