int metal_shmem_open(const char *name, size_t size,
		     struct metal_io_region **result)
{
	struct metal_page_size *ps;
	int fd, error;
	/* Open a log file to record control flow and state for analysis. */
	FILE *logf = fopen("/tmp/logs/195.txt", "a");
	if (logf) {
		fprintf(logf, "ENTER metal_shmem_open: name=%s size=%zu result=%p\n",
			name ? name : "(null)", size, (void *)result);
	}

	error = metal_shmem_open_generic(name, size, result);
	if (logf) {
		fprintf(logf, "after metal_shmem_open_generic: error=%d\n", error);
	}
	if (!error) {
		if (logf) {
			fprintf(logf, "early return (no error): %d\n", error);
			fclose(logf);
		}
		return error;
	}

	error = metal_open(name, 1);
	if (logf) {
		fprintf(logf, "after metal_open: error=%d\n", error);
	}
	if (error < 0) {
		if (logf) {
			fprintf(logf, "metal_open failed name=%s error=%d\n",
				name ? name : "(null)", error);
			fclose(logf);
		}
		metal_log(METAL_LOG_ERROR, "Failed to open shmem file :%s\n", name);
		return error;
	}
	fd = error;
	if (logf) {
		fprintf(logf, "fd=%d\n", fd);
	}

	/* Iterate through page sizes in decreasing order. */
	metal_for_each_page_size_down(ps) {
		if (logf) {
			/* Log the observed fields of ps to demonstrate they are read-only in this function. */
			fprintf(logf, "loop: ps=%p page_size=%zu mmap_flags=%#x page_shift=%d\n",
				(void *)ps, (size_t)ps->page_size, (unsigned int)ps->mmap_flags, (int)ps->page_shift);
		}
		if (ps->page_size > 2 * size) {
			if (logf) {
				fprintf(logf, "skipping ps->page_size=%zu > 2*size=%zu\n",
					(size_t)ps->page_size, (size_t)(2 * size));
			}
			continue;
		}
		error = metal_shmem_try_map(ps, fd, size, result);
		if (logf) {
			fprintf(logf, "after metal_shmem_try_map(ps=%p): error=%d *result=%p\n",
				(void *)ps, error, (void *)(result ? *result : NULL));
		}
		if (!error) {
			if (logf) {
				fprintf(logf, "mapping succeeded with ps=%p\n", (void *)ps);
			}
			break;
		}
	}

	close(fd);
	if (logf) {
		fprintf(logf, "EXIT metal_shmem_open: final error=%d result=%p *result=%p\n",
			error, (void *)result, (void *)(result ? *result : NULL));
		fclose(logf);
	}
	return error;
}
