int metal_shmem_open_generic(const char *name, size_t size,
			     struct metal_io_region **result)
{
	FILE *log = fopen("/tmp/logs/93.txt", "a");
	if (log) {
		fprintf(log, "metal_shmem_open_generic ENTER: name_ptr=%p size=%zu result_ptr=%p\n",
			(void *)name, size, (void *)result);
		fflush(log);
	}

	struct metal_generic_shmem *shmem;
	struct metal_list *node;

	metal_list_for_each(&_metal.common.generic_shmem_list, node) {
		shmem = metal_container_of(node, struct metal_generic_shmem, node);
		if (log) {
			fprintf(log, "ITER: shmem=%p shmem->name_ptr=%p caller_name_ptr=%p\n",
				(void *)shmem, (void *)shmem->name, (void *)name);
			fflush(log);
		}

		/* Call strcmp as original code does, but capture and log result for diagnostics. */
		int cmp = strcmp(shmem->name, name);
		if (log) {
			fprintf(log, "strcmp(shmem->name, name) -> %d\n", cmp);
			fflush(log);
		}
		if (cmp != 0)
			continue;

		/* Names matched; check size as in original implementation. */
		size_t io_sz = metal_io_region_size(&shmem->io);
		if (log) {
			fprintf(log, "match: requested_size=%zu io_region_size=%zu\n", size, io_sz);
			fflush(log);
		}
		if (size <= io_sz) {
			*result = &shmem->io;
			if (log) {
				fprintf(log, "returning success (0). result_io=%p\n", (void *)&shmem->io);
				fclose(log);
			}
			return 0;
		}
	}

	if (log) {
		fprintf(log, "no matching shared memory entry found, returning -ENOENT\n");
		fclose(log);
	}

	return -ENOENT;
}
