static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                               struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;
    FILE *logf = NULL;

    /* Open probe log. It's okay if this fails; we still preserve original logic. */
    logf = fopen("/tmp/logs/317.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER metal_shmem_try_map: ps->page_size=%zu fd=%d size(in)=%zu\n",
                ps ? ps->page_size : 0, fd, size);
    }

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;

    if (logf) {
        fprintf(logf, "Aligned size=%zu pages=%zu page_size=%zu mmap_flags=0x%x\n",
                size, pages, ps->page_size, ps->mmap_flags);
    }

    if (logf) fprintf(logf, "CALL: metal_map(fd=%d, offset=0, size=%zu, prot=1, flags=0x%x)\n",
                      fd, size, ps->mmap_flags);
    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (error) {
        if (logf) {
            fprintf(logf, "metal_map returned error=%d (%s)\n", error, strerror(-error));
            fprintf(logf, "ABOUT TO CALL metal_log: format=\"failed to mmap shmem %ld,0x%x - %s\\n\"\n");
            /* Note: this site passes variadic args: size, ps->mmap_flags, strerror(-error) */
            fprintf(logf, "metal_log invocation details: level=METAL_LOG_WARNING, variadic_args_present=3\n");
            fflush(logf);
        }

        metal_log(METAL_LOG_WARNING,
              "failed to mmap shmem %ld,0x%x - %s\n",
              size, ps->mmap_flags, strerror(-error));
        if (logf) fclose(logf);
        return error;
    }

    if (logf) fprintf(logf, "metal_map succeeded, mem=%p\n", mem);

    if (logf) fprintf(logf, "CALL: mlock(mem=%p, size=%zu)\n", mem, size);
    error = mlock(mem, size);
    if (error) {
        if (logf) {
            fprintf(logf, "mlock returned error=%d (%s)\n", error, strerror(errno));
            fprintf(logf, "ABOUT TO CALL metal_log: format=\"failed to mlock shmem - %s\\n\"\n");
            /* This site passes one variadic arg: strerror(errno) */
            fprintf(logf, "metal_log invocation details: level=METAL_LOG_WARNING, variadic_args_present=1\n");
            fflush(logf);
        }

        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
              strerror(errno));
    }

    phys_size = sizeof(*phys) * pages;
    phys = malloc(phys_size);
    if (!phys) {
        if (logf) {
            fprintf(logf, "malloc for phys failed (size=%zu). Cleaning up and returning -ENOMEM\n", phys_size);
            fclose(logf);
        }
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    io = malloc(sizeof(*io));
    if (!io) {
        if (logf) {
            fprintf(logf, "malloc for io failed. Cleaning up and returning -ENOMEM\n");
            fclose(logf);
        }
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        if (logf) {
            fprintf(logf, "pagemap_fd < 0: will use offset as pa. About to call metal_log with NO variadic args.\n");
            /* This is the invocation of interest for the reported violation: */
            fprintf(logf, "ABOUT TO CALL metal_log: format=\"shmem - failed to get va2pa mapping. use offset as pa.\\n\"\n");
            /* Here the macro invocation in source contains no variadic args; many implementations
             * rely on the GCC extension '##__VA_ARGS__' to remove the preceding comma when
             * __VA_ARGS__ is empty. Logging that fact so it can be inspected. */
            fprintf(logf, "metal_log invocation details: level=METAL_LOG_WARNING, variadic_args_present=0 (no additional args)\n");
            fflush(logf);
        }

        metal_log(METAL_LOG_WARNING,
        "shmem - failed to get va2pa mapping. use offset as pa.\n");
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
    } else {
        if (logf) fprintf(logf, "pagemap_fd >= 0: resolving virtual to physical for %zu pages\n", pages);
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            if (logf) fprintf(logf, "CALL: metal_virt2phys(virt+%zu) for page %zu\n", offset, page);
            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0) {
                phys[page] = METAL_BAD_OFFSET;
                if (logf) fprintf(logf, "metal_virt2phys failed for page %zu, marking METAL_BAD_OFFSET\n", page);
            } else {
                if (logf) fprintf(logf, "metal_virt2phys succeeded for page %zu\n", page);
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
            &metal_shmem_io_ops);
    }
    *result = io;

    if (logf) {
        fprintf(logf, "EXIT metal_shmem_try_map: returning 0, io=%p\n", (void *)io);
        fclose(logf);
    }

    return 0;
}
