static int metal_shmem_try_map(struct metal_page_size *ps, int fd, size_t size,
                               struct metal_io_region **result)
{
    size_t pages, page, phys_size;
    struct metal_io_region *io;
    metal_phys_addr_t *phys;
    uint8_t *virt;
    void *mem;
    int error;
    size_t orig_size = size;
    FILE *log = NULL;

    /* Attempt to open the log file. If it fails, continue without aborting. */
    log = fopen("/tmp/logs/317.txt", "a");
    if (log) {
        fprintf(log, "ENTER metal_shmem_try_map: ps=%p page_size=%zu fd=%d requested_size=%zu\n",
                (void *)ps, (ps ? ps->page_size : 0), fd, orig_size);
    }

    size = metal_align_up(size, ps->page_size);
    pages = size / ps->page_size;
    if (log) {
        fprintf(log, "aligned size=%zu page_size=%zu pages=%zu\n",
                size, ps->page_size, pages);
    }

    error = metal_map(fd, 0, size, 1, ps->mmap_flags, &mem);
    if (log) {
        fprintf(log, "metal_map(fd=%d, offset=0, size=%zu, prot=1, flags=0x%x) -> error=%d mem=%p\n",
                fd, size, ps->mmap_flags, error, mem);
    }
    if (error) {
        metal_log(METAL_LOG_WARNING,
                  "failed to mmap shmem %ld,0x%x - %s\n",
                  size, ps->mmap_flags, strerror(-error));
        if (log) {
            fprintf(log, "FAILED metal_map: error=%d strerror=%s\n", error, strerror(-error));
            fclose(log);
        }
        return error;
    }

    error = mlock(mem, size);
    if (log) {
        fprintf(log, "mlock(mem=%p, size=%zu) -> error=%d\n", mem, size, error);
    }
    if (error) {
        metal_log(METAL_LOG_WARNING, "failed to mlock shmem - %s\n",
                  strerror(errno));
        if (log) {
            fprintf(log, "WARNING: mlock failed errno=%d strerror=%s\n", errno, strerror(errno));
        }
    }

    phys_size = sizeof(*phys) * pages;
    if (log) {
        fprintf(log, "allocating phys array: phys_size=%zu (pages=%zu)\n", phys_size, pages);
    }
    phys = malloc(phys_size);
    if (!phys) {
        if (log) {
            fprintf(log, "malloc(phys_size=%zu) failed\n", phys_size);
            fclose(log);
        }
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    io = malloc(sizeof(*io));
    if (log) {
        fprintf(log, "malloc io -> %p\n", (void *)io);
    }
    if (!io) {
        if (log) {
            fprintf(log, "malloc(io) failed, freeing phys and unmapping mem\n");
            fclose(log);
        }
        free(phys);
        metal_unmap(mem, size);
        return -ENOMEM;
    }

    if (_metal.pagemap_fd < 0) {
        phys[0] = 0;
        metal_log(METAL_LOG_WARNING,
                  "shmem - failed to get va2pa mapping. use offset as pa.\n");
        if (log) {
            fprintf(log, "pagemap_fd=%d (<0), setting phys[0]=0 and using offset as PA\n", _metal.pagemap_fd);
        }
        metal_io_init(io, mem, phys, size, -1, 0, &metal_shmem_io_ops);
        if (log) {
            fprintf(log, "metal_io_init called (pagemap_fd<0): io=%p mem=%p phys=%p size=%zu page_shift=%d\n",
                    (void *)io, mem, (void *)phys, size, -1);
            fclose(log);
        }
    } else {
        if (log) {
            fprintf(log, "pagemap_fd=%d, attempting virt->phys translation for each page\n", _metal.pagemap_fd);
        }
        for (virt = mem, page = 0; page < pages; page++) {
            size_t offset = page * ps->page_size;

            error = metal_virt2phys(virt + offset, &phys[page]);
            if (error < 0)
                phys[page] = METAL_BAD_OFFSET;

            if (log) {
                if (error < 0) {
                    fprintf(log, "page %zu: virt=%p offset=%zu -> metal_virt2phys error=%d, phys[%zu]=BAD_OFFSET\n",
                            page, (void *)(virt + offset), offset, error, page);
                } else {
                    fprintf(log, "page %zu: virt=%p offset=%zu -> phys[%zu]=0x%llx\n",
                            page, (void *)(virt + offset), offset, page, (unsigned long long)phys[page]);
                }
            }
        }
        metal_io_init(io, mem, phys, size, ps->page_shift, 0,
                      &metal_shmem_io_ops);
        if (log) {
            fprintf(log, "metal_io_init called: io=%p mem=%p phys=%p size=%zu page_shift=%d\n",
                    (void *)io, mem, (void *)phys, size, ps->page_shift);
            fclose(log);
        }
    }
    *result = io;

    /* Final report */
    if (!log) {
        /* If log couldn't be opened earlier, write a minimal trace via metal_log. */
        metal_log(METAL_LOG_DEBUG, "metal_shmem_try_map: completed, io=%p result set\n", (void *)io);
    }

    return 0;
}
